mapper.select:
SELECT  COUNT(1)
  FROM  TB_USER
 WHERE  USER_ID     = #{userId}

   AND  USER_AUTH_KEY = #{userAuthKey}
   AND  USE_YN      = 'Y'

mapper.update:
UPDATE  TB_USER
   SET  USER_AUTH_KEY   = #{userAuthKey}
 WHERE  USER_ID         = #{userId}

mapper.insert:
INSERT INTO     TB_SCRAP_ERROR_LOG (
        SCRAP_DTTM	, VAN_CD		, CHAIN_NO		, CHAIN_NM		
        , LOGIN_ID	, LOGIN_PWD		, ERROR_MSG
) VALUES (                                   
        NOW()	        , #{vanCd}		, #{chainNo}		, #{chainNm}	
        , #{loginId}    , #{loginPwd}	        , #{errorMsg}     
)

INSERT INTO     TB_SCRAP_LOG (
        CHAIN_NO        , SCRAP_GB      , VAN_CD        , LAST_SCRAP_DTTM
        , UPLOAD_CNT    , SAVE_CNT      , DUP_CNT       , RSLT_MSG
) VALUES (                                   
        #{chain_no}	, #{scrap_gb}   , #{van_cd}     , NOW()
        , #{upload_cnt} , #{save_cnt}	, #{dup_cnt}    , #{rslt_msg}     
)

mapper.select:
SELECT TC.CHAIN_NO AS chainNo , TC.CHAIN_NM AS chainNm , TC.BIZ_NO AS bizNo , VN.VAN_CD AS vanCd , CD.CODE_NM AS vanNm , VN.VAN_ID AS loginId , VN.VAN_PWD AS loginPwd FROM TB_CHAIN TC JOIN TB_CHAIN_VAN VN ON VN.CHAIN_NO = TC.CHAIN_NO AND VN.USE_YN = 'Y' AND VN.VAN_CD = #{vanCd} AND VN.USE_YN = 'Y' JOIN TB_CODE CD ON CD.CODE = VN.VAN_CD AND CD.CODE_GRP_CD = 'VAN_CD' AND CD.USE_YN = 'Y' WHERE TC.SVC_STAT IN ('O', 'R')
AND TC.CHAIN_NO = #{chainNo}
AND NOT EXISTS( SELECT 1 FROM TB_SCRAP_LOG SL WHERE SL.CHAIN_NO = TC.CHAIN_NO AND SL.SCRAP_GB = 'VAN' AND SL.VAN_CD = #{vanCd} AND SL.LAST_SCRAP_DTTM >= DATE_SUB(NOW(), INTERVAL 90 MINUTE) ) ORDER BY TC.CHAIN_NO

mapper.insert:
INSERT INTO tb_scrap_van_data(
        TRADE_NO        , VAN_CD        , CHAIN_NO      , CARD_REG_NO   , MID
        , CONF_NO       , CONF_GB       , CONF_RSLT     , CONF_DTTM     , CONF_TYPE     , CARD_TYPE
        , CARD_NO       , CARD_ACQ      , CARD_ISSUE    , CONF_AMT      , INSTALL_MON   , ORG_CONF_DT
        , STORE_ID      , BIZ_NO        , BIZ_NM        , CAT_ID        , USE_YN        , ENT_DTTM
) VALUES (
        #{tradeNo}      , #{vanCd}      , #{chainNo}    , #{cardRegNo}  , #{mid}
        , #{confNo}     , #{confGb}     , #{confRslt}   , #{confDttm}   , #{confType}   , #{cardType}
        , #{cardNo}     , #{cardAcq}    , #{cardIssue}  , #{confAmt}    , #{installMon} , #{orgConfDt}
        , #{storeId}    , #{bizNo}      , #{bizNm}      , #{catId}      , #{useYn}      , NOW()
)
