mapper.select:
SELECT  COUNT(1)
FROM  tb_user
WHERE  user_id = #{user_id}

SELECT SQL_CALC_FOUND_ROWS TA.agency_cd , TA.agency_nm , TA.biz_no , TA.law_no , TA.uptae , TA.upjong , TA.email , TA.corp_zip_no , TA.corp_addr , TA.corp_addr_dtl , TA.corp_tel_no , TA.ceo_id , TU.USER_NM AS ceo_nm , TU.ZIP_NO AS ceo_zip_no , TU.ADDR AS ceo_addr , TU.ADDR_DTL AS ceo_addr_dtl , TU.HP_NO AS ceo_tel_no , TA.assure_amt , TA.insure_amt , TA.ass_sdate , TA.ass_edate , TA.remark , IFNULL(TA.cont_dt, '') AS cont_dt , IFNULL(TA.svc_stat, '') AS svc_stat , TA.svc_fee_rate, TA.crd_fee_rate, TA.loan_fee_rate , TA.bank_cd , TA.account_no, TA.depositor, TA.yubo_limit, TA.yubo_rate, TA.tot_yubo_amt , TA.use_yn , IFNULL((SELECT COUNT(1) FROM TB_CHAIN WHERE AGENCY_CD = TA.AGENCY_CD AND USE_YN = 'Y' AND SVC_STAT IN ('R', 'O')), 0) AS chain_cnt , IFNULL(TA.upt_user_id, TA.ent_user_id) as upt_user_id , DATE_FORMAT(IFNULL(TA.upt_dttm, TA.ent_dttm), '%Y-%m-%d %H:%i:%s') as upt_dttm FROM TB_AGENCY TA JOIN TB_USER TU ON TU.USER_ID = TA.CEO_ID WHERE 1 = 1
AND TA.AGENCY_CD = #{schgb_agency_val}
AND TA.AGENCY_NM LIKE CONCAT('%', #{schgb_agency_val}, '%')
AND TA.USE_YN = #{sch_use_yn}
ORDER BY ${sidx} ${sord}
ORDER BY agency_cd DESC
LIMIT ${start}, ${end}

mapper.insert:
INSERT INTO tb_agency (
        agency_cd             , agency_nm             , biz_no                , law_no            
        , uptae                 , upjong                , email
        , corp_zip_no           , corp_addr             , corp_addr_dtl         , corp_tel_no                       
        , ceo_id                , remark                , use_yn                
        , ent_user_id           , ent_dttm
) VALUES (
        #{agency_cd}            , #{agency_nm}          , #{biz_no}             , #{law_no}         
        , #{uptae}              , #{upjong}             , #{email}
        , #{corp_zip_no}        , #{corp_addr}          , #{corp_addr_dtl}      , #{corp_tel_no}
        , #{ceo_id}             , #{remark}             , #{use_yn}  
        , #{ent_user_id}        , NOW()
)
