mapper.select:
SELECT  COUNT(1)
    FROM  tb_user
    WHERE  user_id = #{user_id}

SELECT TC.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , IFNULL(TC.CORP_CD, '') AS corp_cd , IFNULL(CO.CORP_NM, '') AS corp_nm , IFNULL(TC.AGENCY_NO, '') AS agency_no , IFNULL(AG.AGENCY_NM, '') AS agency_nm , IFNULL(TC.BIZ_NO, '') AS biz_no , IFNULL(TC.LAW_NO, '') AS law_no , IFNULL(TC.UPTAE, '') AS uptae , IFNULL(TC.UPJONG, '') AS upjong , IFNULL(TC.CHAIN_TEL_NO, '') AS chain_tel_no , IFNULL(TC.CHAIN_ZIP_NO, '') AS chain_zip_no , IFNULL(TC.CHAIN_ADDR, '') AS chain_addr , IFNULL(TC.CHAIN_ADDR_DTL, '') AS chain_addr_dtl , IFNULL(TC.CEO_ID, '') AS ceo_id , IFNULL(TU.USER_NM, '') AS ceo_nm , IFNULL(TC.CEO_BIRTH_DT, '') AS ceo_birth_dt , IFNULL(TC.CEO_REG_SEX, '') AS ceo_reg_sex , IFNULL(TC.CEO_REG_NO, '') AS ceo_reg_no , IFNULL(TU.TEL_NO, '') AS ceo_tel_no , IFNULL(TU.ZIP_NO, '') AS ceo_zip_no , IFNULL(TU.ADDR, '') AS ceo_addr , IFNULL(TU.ADDR_DTL, '') AS ceo_addr_dtl , IFNULL(TC.EMAIL, '') AS email , IFNULL(TC.ABANK_ACCOUNT_TP, '') AS abank_account_tp , IFNULL(TC.ABANK_CD, '') AS abank_cd , IFNULL(TC.ABANK_ACCOUNT, '') AS abank_account , IFNULL(TC.ABANK_ACCOUNT_NO, '') AS abank_account_no , IFNULL(TC.ABANK_SPEED_YN, '') AS abank_speed_yn , IFNULL(TC.ABANK_PWD, '') AS abank_pwd , IFNULL(TC.ABANK_DEPOSITOR, '') AS abank_depositor , IFNULL(TC.ABANK_CHECK_YN, '') AS abank_check_yn , IFNULL(TC.ABANK_CHECK_ID, '') AS abank_check_id , IFNULL(TC.ABANK_CHECK_DT, '') AS abank_check_dt , IFNULL(TC.ABANK_LINK_STAT, '') AS abank_link_stat , IFNULL(TC.ABANK_LINK_AGREE_DT, '') AS abank_link_agree_dt , IFNULL(TC.ABANK_LINK_PAYER_NO, '') AS abank_link_payer_no , IFNULL(TC.ABANK_LAST_REMIT_DTTM, '') AS abank_last_remit_dttm , IFNULL(TC.ABANK_SEARCH_ID, '') AS abank_search_id , IFNULL(TC.ABANK_SEARCH_PWD, '') AS abank_search_pwd , IFNULL(TC.BBANK_CD, '') AS bbank_cd , IFNULL(TC.BBANK_ACCOUNT, '') AS bbank_account , IFNULL(TC.BBANK_DEPOSITOR, '') AS bbank_depositor , IFNULL(TC.BBANK_CHECK_YN, '') AS bbank_check_yn , IFNULL(TC.BBANK_CHECK_ID, '') AS bbank_check_id , IFNULL(TC.BBANK_CHECK_DT, '') AS bbank_check_dt , IFNULL(TC.AUTO_REMIT_YN, '') AS auto_remit_yn , IFNULL(TC.SVC_STAT, '') AS svc_stat , GET_CODE_NM('SVC_STAT', TC.SVC_STAT) AS svc_stat_nm , IFNULL(TC.SVC_START_DT, '') AS svc_start_dt , IFNULL(TC.SVC_END_DT, '') AS svc_end_dt , IFNULL(TC.CRD_RATE, 0) AS crd_rate , IFNULL(TC.CRD_DELAY_RATE, 0) AS crd_delay_rate , IFNULL(TC.SVC_RATE, 0) AS svc_rate , IFNULL(TC.ETC_RATE, 0) AS etc_rate , IFNULL(TC.TOT_LIMIT_AMT, 0) AS tot_limit_amt , IFNULL(TC.UNIT_LIMIT_AMT, 0) AS unit_limit_amt , IFNULL(TC.DAY_USE_RATE, 0) AS day_use_rate , IFNULL(TC.DAY_USE_AMT, 0) AS day_use_amt , IFNULL(TC.TOT_USE_AMT, 0) AS tot_use_amt , IFNULL(TC.COLLECT_TYPE, '') AS collect_type , IFNULL(TC.COLLECT_TRANS, '') AS collect_trans , IFNULL(TC.REMIT_TYPE, '') AS remit_type , IFNULL(TC.REMIT_CYCLE, '') AS remit_cycle , IFNULL(TC.REMIT_STAT, '') AS remit_stat , GET_CODE_NM('REMIT_STAT', TC.REMIT_STAT) AS remit_stat_nm , IFNULL(TC.REMIT_STAT_CD, '') AS remit_stat_cd , IFNULL(TC.REMIT_STAT_DATE, '') AS remit_stat_date , IFNULL(TC.REMIT_OVER_STAT, '') AS remit_over_stat , IFNULL(TC.REMIT_AUTO_STAT, '') AS remit_auto_stat , IFNULL(TC.REMIT_TRANS_FEE, '') AS remit_trans_fee , IFNULL(TC.REMIT_START_DT, '') AS remit_start_dt , IFNULL(TC.REMIT_END_DT, '') AS remit_end_dt , IFNULL(TC.REMARK, '') AS remark , IFNULL(TC.BK_DIV, '') AS bk_div , IFNULL(TC.CARDSALES_ID, '') AS cardsales_id , IFNULL(TC.CARDSALES_PWD, '') AS cardsales_pwd , TC.WORK_CHECK_FLAG AS work_check_flag , TC.WORK_CHECK_MM AS work_check_mm , TC.WORK_START_TM AS work_start_tm , TC.WORK_END_TM AS work_end_tm , TC.WORK_HOLIDAY_FLAG AS work_holiday_flag , TC.LAST_CHECK_DTTM AS last_check_dttm , IFNULL(TC.USE_YN, 'N') AS use_yn , IFNULL(TC.UPT_USER_ID, TC.ENT_USER_ID) AS upt_user_id , DATE_FORMAT(IFNULL(TC.UPT_DTTM, TC.ENT_DTTM), '%Y-%m-%d %H:%i:%s') AS upt_dttm FROM TB_CHAIN TC JOIN TB_CORP CO ON CO.CORP_CD = TC.CORP_CD JOIN TB_AGENCY AG ON AG.AGENCY_NO = TC.AGENCY_NO JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID
WHERE 1 = 1
AND TC.CORP_NO = #{sch_corp_no}
AND TC.AGENCY_NO = #{sch_agency_no}
AND TC.CHAIN_NO = #{schgb_chain_val}
AND TC.CHAIN_NM LIKE CONCAT('%', #{schgb_chain_val}, '%')
AND TC.USE_YN = #{sch_use_yn}
ORDER BY ${sidx} ${sord}
ORDER BY ent_dttm DESC

mapper.insert:
INSERT INTO tb_chain (
    CHAIN_NO        , CHAIN_NM          , CORP_CD           , AGENCY_NO
    , BIZ_NO        , LAW_NO            , UPTAE             , UPJONG
    , CHAIN_TEL_NO  , CHAIN_ZIP_NO      , CHAIN_ADDR        , CHAIN_ADDR_DTL
    , CEO_ID        , CEO_BIRTH_DT      , CEO_REG_NO        , EMAIL
    , REMARK                    
    , ENT_DTTM      , ENT_USER_ID
) VALUES (
    #{chain_no}         , #{chain_nm}       , #{corp_cd}        , #{agency_no}
    , #{biz_no}         , #{law_no}         , #{uptae}          , #{upjong}
    , #{chain_tel_no}   , #{chain_zip_no}   , #{chain_addr}     , #{chain_addr_dtl}
    , #{ceo_id}         , #{ceo_birth_dt}   , #{ceo_reg_no}     , #{email}
    , #{remark}
    , NOW()             , #{ent_user_id}
)

mapper.update:
UPDATE tb_chain SET CHAIN_NM = #{chain_nm}, CORP_CD = #{corp_cd}, AGENCY_NO = #{agency_no}, BIZ_NO = #{biz_no}, LAW_NO = #{law_no}, UPTAE = #{uptae}, UPJONG = #{upjong}, CHAIN_TEL_NO = #{chain_tel_no}, CHAIN_ZIP_NO = #{chain_zip_no}, CHAIN_ADDR = #{chain_addr}, CHAIN_ADDR_DTL = #{chain_addr_dtl},
CEO_BIRTH_DT = #{ceo_birth_dt},
EMAIL = #{email}, REMARK = #{remark}, UPT_USER_ID = #{upt_user_id}, UPT_DTTM = NOW() WHERE CHAIN_NO = #{chain_no}
