mapper.select:
/*AgencyFeeMapper.getAgencyFeeSummary */ SELECT SQL_CALC_FOUND_ROWS AF.AGENCY_CD AS agency_cd , AG.AGENCY_NM AS agency_nm , AF.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , TU.USER_NM AS ceo_nm , IFNULL(SUM(AF.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(AF.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(AF.ORG_CARD_RESV_AMT), 0) AS org_card_resv_amt , IFNULL(SUM(AF.RESV_BASE_AMT), 0) AS resv_base_amt , IFNULL(SUM(AF.RESV_SVC_AMT), 0) AS resv_svc_amt , IFNULL(SUM(AF.RESV_CRD_AMT), 0) AS resv_crd_amt , IFNULL(SUM(AF.BANK_IN_AMT), 0) AS bank_in_amt , IFNULL(SUM(AF.BANK_IN_BASE_AMT), 0) AS bank_in_base_amt , IFNULL(SUM(AF.BANK_IN_SVC_AMT), 0) AS bank_in_svc_amt , IFNULL(SUM(AF.BANK_IN_CRD_AMT), 0) AS bank_in_crd_amt , IFNULL(SUM(AF.EXC_AMT), 0) AS exc_amt , IFNULL(SUM(AF.SUB_BASE_AMT), 0) AS sub_base_amt , IFNULL(SUM(AF.SUB_SVC_AMT), 0) AS sub_svc_amt , IFNULL(SUM(AF.SUB_CRD_AMT), 0) AS sub_crd_amt , IFNULL(SUM(AF.SUB_AMT), 0) AS sub_amt , IFNULL(SUM(AF.BIZ_CRD_AMT), 0) AS biz_crd_amt , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) AS agent_svc_fee , IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) AS agent_crd_fee , IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_loan_fee , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_tot_fee FROM VW_AGENCY_FEE_LIST AF STRAIGHT_JOIN TB_AGENCY AG ON AG.AGENCY_CD = AF.AGENCY_CD STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = AF.CHAIN_NO
AND TC.CORP_CD = #{userCorpCd}
AND 1 = 1
AND TC.AGENCY_CD = #{userCorpCd}
AND 1 = 0
STRAIGHT_JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID WHERE AF.ADJUST_DATE BETWEEN CONCAT(#{sch_month}, '-01') AND CONCAT(#{sch_month}, '-31')
AND AF.AGENCY_CD = #{sch_agency_cd}
GROUP BY AF.AGENCY_CD, AG.AGENCY_NM, AF.CHAIN_NO, TC.CHAIN_NM, TU.USER_NM
ORDER BY ${sidx} ${sord}
ORDER BY 1
LIMIT ${start}, ${end}

/*AgencyFeeMapper.getAgencyFeeSummaryTotal */ SELECT SQL_CALC_FOUND_ROWS IFNULL(SUM(AF.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(AF.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(AF.ORG_CARD_RESV_AMT), 0) AS org_card_resv_amt , IFNULL(SUM(AF.RESV_BASE_AMT), 0) AS resv_base_amt , IFNULL(SUM(AF.RESV_SVC_AMT), 0) AS resv_svc_amt , IFNULL(SUM(AF.RESV_CRD_AMT), 0) AS resv_crd_amt , IFNULL(SUM(AF.BANK_IN_AMT), 0) AS bank_in_amt , IFNULL(SUM(AF.BANK_IN_BASE_AMT), 0) AS bank_in_base_amt , IFNULL(SUM(AF.BANK_IN_SVC_AMT), 0) AS bank_in_svc_amt , IFNULL(SUM(AF.BANK_IN_CRD_AMT), 0) AS bank_in_crd_amt , IFNULL(SUM(AF.EXC_AMT), 0) AS exc_amt , IFNULL(SUM(AF.SUB_BASE_AMT), 0) AS sub_base_amt , IFNULL(SUM(AF.SUB_SVC_AMT), 0) AS sub_svc_amt , IFNULL(SUM(AF.SUB_CRD_AMT), 0) AS sub_crd_amt , IFNULL(SUM(AF.SUB_AMT), 0) AS sub_amt , IFNULL(SUM(AF.BIZ_CRD_AMT), 0) AS biz_crd_amt , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) AS agent_svc_fee , IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) AS agent_crd_fee , IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_loan_fee , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_tot_fee FROM VW_AGENCY_FEE_LIST AF STRAIGHT_JOIN TB_AGENCY AG ON AG.AGENCY_CD = AF.AGENCY_CD STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = AF.CHAIN_NO
AND TC.CORP_CD = #{userCorpCd}
AND 1 = 1
AND TC.AGENCY_CD = #{userCorpCd}
AND 1 = 0
STRAIGHT_JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID WHERE AF.ADJUST_DATE BETWEEN CONCAT(#{sch_month}, '-01') AND CONCAT(#{sch_month}, '-31')
AND AF.AGENCY_CD = #{sch_agency_cd}

/* AgencyFeeMapper.getAgencyFeeList */ SELECT SQL_CALC_FOUND_ROWS AF.ADJUST_DATE AS adjust_date , IFNULL(SUM(AF.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(AF.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(AF.ORG_CARD_RESV_AMT), 0) AS org_card_resv_amt , IFNULL(SUM(AF.RESV_BASE_AMT), 0) AS resv_base_amt , IFNULL(SUM(AF.RESV_SVC_AMT), 0) AS resv_svc_amt , IFNULL(SUM(AF.RESV_CRD_AMT), 0) AS resv_crd_amt , IFNULL(SUM(AF.BANK_IN_AMT), 0) AS bank_in_amt , IFNULL(SUM(AF.BANK_IN_BASE_AMT), 0) AS bank_in_base_amt , IFNULL(SUM(AF.BANK_IN_SVC_AMT), 0) AS bank_in_svc_amt , IFNULL(SUM(AF.BANK_IN_CRD_AMT), 0) AS bank_in_crd_amt , IFNULL(SUM(AF.EXC_AMT), 0) AS exc_amt , IFNULL(SUM(AF.SUB_BASE_AMT), 0) AS sub_base_amt , IFNULL(SUM(AF.SUB_SVC_AMT), 0) AS sub_svc_amt , IFNULL(SUM(AF.SUB_CRD_AMT), 0) AS sub_crd_amt , IFNULL(SUM(AF.SUB_AMT), 0) AS sub_amt , IFNULL(SUM(AF.BIZ_CRD_AMT), 0) AS biz_crd_amt , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) AS agent_svc_fee , IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) AS agent_crd_fee , IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_loan_fee , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_tot_fee FROM VW_AGENCY_FEE_LIST AF STRAIGHT_JOIN TB_AGENCY AG ON AG.AGENCY_CD = AF.AGENCY_CD STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = AF.CHAIN_NO
AND TC.CORP_CD = #{userCorpCd}
AND 1 = 1
AND TC.AGENCY_CD = #{userCorpCd}
AND 1 = 0
STRAIGHT_JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID WHERE AF.ADJUST_DATE BETWEEN CONCAT(#{sch_month}, '-01') AND CONCAT(#{sch_month}, '-31') AND AF.CHAIN_NO = #{sch_chain_no} GROUP BY AF.ADJUST_DATE
ORDER BY ${sidx} ${sord}
ORDER BY 1
LIMIT ${start}, ${end}

/*AgencyFeeMapper.getAgencyFeeListTotal */ SELECT SQL_CALC_FOUND_ROWS IFNULL(SUM(AF.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(AF.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(AF.ORG_CARD_RESV_AMT), 0) AS org_card_resv_amt , IFNULL(SUM(AF.RESV_BASE_AMT), 0) AS resv_base_amt , IFNULL(SUM(AF.RESV_SVC_AMT), 0) AS resv_svc_amt , IFNULL(SUM(AF.RESV_CRD_AMT), 0) AS resv_crd_amt , IFNULL(SUM(AF.BANK_IN_AMT), 0) AS bank_in_amt , IFNULL(SUM(AF.BANK_IN_BASE_AMT), 0) AS bank_in_base_amt , IFNULL(SUM(AF.BANK_IN_SVC_AMT), 0) AS bank_in_svc_amt , IFNULL(SUM(AF.BANK_IN_CRD_AMT), 0) AS bank_in_crd_amt , IFNULL(SUM(AF.EXC_AMT), 0) AS exc_amt , IFNULL(SUM(AF.SUB_BASE_AMT), 0) AS sub_base_amt , IFNULL(SUM(AF.SUB_SVC_AMT), 0) AS sub_svc_amt , IFNULL(SUM(AF.SUB_CRD_AMT), 0) AS sub_crd_amt , IFNULL(SUM(AF.SUB_AMT), 0) AS sub_amt , IFNULL(SUM(AF.BIZ_CRD_AMT), 0) AS biz_crd_amt , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) AS agent_svc_fee , IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) AS agent_crd_fee , IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_loan_fee , IFNULL(ROUND(SUM(AF.AGENCY_SVC_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_CRD_FEE)), 0) + IFNULL(ROUND(SUM(AF.AGENCY_LOAN_FEE)), 0) AS agent_tot_fee FROM VW_AGENCY_FEE_LIST AF JOIN TB_AGENCY AG ON AG.AGENCY_CD = AF.AGENCY_CD JOIN TB_CHAIN TC ON TC.CHAIN_NO = AF.CHAIN_NO
AND TC.CORP_CD = #{userCorpCd}
AND 1 = 1
AND TC.AGENCY_CD = #{userCorpCd}
AND 1 = 0
JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID WHERE AF.ADJUST_DATE BETWEEN CONCAT(#{sch_month}, '-01') AND CONCAT(#{sch_month}, '-31') AND AF.CHAIN_NO = #{sch_chain_no}

/*AgencyFeeMapper.getChainTaxExcel */ SELECT DATE_FORMAT(LAST_DAY(DATE(CONCAT('2025-07', '-01'))), '%Y-%m-%d') AS tax_dt , VT.CHAIN_NO AS chain_no , TC.BIZ_NO AS biz_no , TC.CHAIN_NM AS chain_nm , TU.USER_NM AS boss_nm , CONCAT(TC.CHAIN_ADDR, ' ', IFNULL(TC.CHAIN_ADDR_DTL, '')) AS chain_addr , IFNULL(TC.EMAIL, '') AS email , IFNULL(TC.UPTAE, '') AS uptae , IFNULL(TC.UPJONG, '') AS upjong , IFNULL(RESV_SVC_AMT, 0) AS svc_amt , ROUND(IFNULL(RESV_SVC_AMT, 0) / 1.1) AS svc_sales_amt , IFNULL(RESV_SVC_AMT, 0) - (ROUND(IFNULL(RESV_SVC_AMT, 0) / 1.1)) AS svc_vat_amt FROM ( SELECT CHAIN_NO , IFNULL(SUM(CONF_CNT), 0) AS CONF_CNT , IFNULL(SUM(CONF_AMT), 0) AS CONF_AMT , IFNULL(SUM(ORG_CARD_RESV_AMT), 0) AS ORG_CARD_RESV_AMT , IFNULL(SUM(RESV_BASE_AMT), 0) AS RESV_BASE_AMT , IFNULL(SUM(RESV_SVC_AMT), 0) AS RESV_SVC_AMT , IFNULL(SUM(RESV_CRD_AMT), 0) AS RESV_CRD_AMT , IFNULL(SUM(BANK_IN_AMT), 0) AS BANK_IN_AMT , IFNULL(SUM(BANK_IN_BASE_AMT), 0) AS BANK_IN_BASE_AMT , IFNULL(SUM(BANK_IN_SVC_AMT), 0) AS BANK_IN_SVC_AMT , IFNULL(SUM(BANK_IN_CRD_AMT), 0) AS BANK_IN_CRD_AMT , IFNULL(SUM(EXC_AMT), 0) AS EXC_AMT , IFNULL(SUM(SUB_BASE_AMT), 0) AS SUB_BASE_AMT , IFNULL(SUM(SUB_SVC_AMT), 0) AS SUB_SVC_AMT , IFNULL(SUM(SUB_CRD_AMT), 0) AS SUB_CRD_AMT , IFNULL(SUM(SUB_AMT), 0) AS SUB_AMT , IFNULL(SUM(BIZ_CRD_AMT), 0) AS BIZ_CRD_AMT , IFNULL(ROUND(SUM(AGENCY_SVC_FEE)), 0) AS AGENCY_SVC_FEE , IFNULL(ROUND(SUM(AGENCY_CRD_FEE)), 0) AS AGENCY_CRD_FEE , IFNULL(ROUND(SUM(AGENCY_LOAN_FEE)), 0) AS AGENCY_LOAN_FEE , IFNULL(ROUND(SUM(AGENCY_SVC_FEE)), 0) + IFNULL(ROUND(SUM(AGENCY_CRD_FEE)), 0) + IFNULL(ROUND(SUM(AGENCY_LOAN_FEE)), 0) AS AGENCY_TOT_FEE FROM VW_AGENCY_FEE_LIST WHERE ADJUST_DATE BETWEEN CONCAT(#{sch_month}, '-01') AND DATE_FORMAT(LAST_DAY(DATE(CONCAT(#{sch_month}, '-01'))), '%Y-%m-%d')
AND AGENCY_CD = #{sch_agency_cd}
GROUP BY CHAIN_NO ) VT JOIN TB_CHAIN TC ON TC.CHAIN_NO = VT.CHAIN_NO
AND TC.CORP_CD = #{userCorpCd}
AND 1 = 1
AND TC.AGENCY_CD = #{userCorpCd}
AND 1 = 0
JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID
ORDER BY ${sidx} ${sord}
ORDER BY 1
LIMIT ${start}, ${end}
