mapper.select:
SELECT SQL_CALC_FOUND_ROWS TC.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , TU.USER_NM AS ceo_nm , TC.BIZ_NO AS biz_no , TC.CHAIN_ADDR AS chain_addr , TC.CHAIN_TEL_NO AS chain_tel_no , TU.TEL_NO AS ceo_tel_no FROM TB_CHAIN TC JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID WHERE TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('O', 'R') AND TC.CHAIN_NO != #{sch_help_chain_no} AND NOT EXISTS (SELECT 1 FROM TB_CHAIN_LINK L WHERE L.CHAIN_NO = #{sch_help_chain_no} AND L.LINK_CHAIN_NO = TC.CHAIN_NO )
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_help_chain_nm}, '%')
ORDER BY ${sidx} ${sord}
ORDER BY 1 DESC
LIMIT ${start}, ${end}

SELECT  COUNT(1)
        FROM  tb_user
      WHERE  user_id = #{user_id}
