mapper.select:
/* DepositController.getDepositSummary */ SELECT SQL_CALC_FOUND_ROWS VW.CHAIN_NO as chain_no , TC.CHAIN_NM as chain_nm , TC.BIZ_NO as biz_no , IFNULL(VW.TOT_CNT, 0) as tot_cnt , IFNULL(VW.CONF_CNT, 0) as conf_cnt , IFNULL(VW.CNCL_CNT, 0) as cncl_cnt , IFNULL(VW.TOT_AMT, 0) as tot_amt , IFNULL(VW.CONF_AMT, 0) as conf_amt , IFNULL(VW.CNCL_AMT, 0) as cncl_amt , IFNULL(VW.CARD_FEE_AMT, 0) as card_fee_amt , IFNULL(VW.CARD_RESV_AMT, 0) as card_resv_amt , IFNULL(VW.CARD_RESV_AMT_REMIT, 0) as card_resv_amt_remit , IFNULL(VW.DEPO_AMT, 0) as depo_amt , IFNULL(VW.DEPO_GAP_AMT_REMIT, 0) as depo_gap_amt_remit , IFNULL(VW.DEPO_GAP_AMT, 0) as depo_gap_amt , IFNULL(VW.REMIT_AMT, 0) as remit_amt , IFNULL(CR.CREDIT_AMT, 0) as credit_amt , IFNULL(VW.DEPO_AMT, 0) - IFNULL(CR.CREDIT_AMT, 0) as credit_gap_amt FROM ( SELECT WD.CHAIN_NO , SUM(TOT_CNT) AS TOT_CNT , SUM(CONF_CNT) AS CONF_CNT , SUM(CNCL_CNT) AS CNCL_CNT , SUM(CONF_AMT) AS CONF_AMT , SUM(CNCL_AMT) AS CNCL_AMT , SUM(TOT_SALES_AMT) AS TOT_AMT , SUM(CARD_FEE_AMT) AS CARD_FEE_AMT , SUM(CARD_RESV_AMT) AS CARD_RESV_AMT , SUM(CARD_RESV_AMT_REMIT) AS CARD_RESV_AMT_REMIT , SUM(DEPO_AMT) AS DEPO_AMT , SUM(DEPO_GAP_AMT_REMIT) AS DEPO_GAP_AMT_REMIT , SUM(DEPO_GAP_AMT) AS DEPO_GAP_AMT , SUM(SVC_FEE_AMT) AS SVC_FEE_AMT , SUM(CRD_FEE_AMT) AS CRD_FEE_AMT , SUM(WD_BASE_AMT) AS WD_BASE_AMT , SUM(REMIT_AMT) AS REMIT_AMT FROM VW_DEPOSIT_RESV_LIST WD WHERE WD.CARD_RESV_DATE = #{sch_resv_date} GROUP BY WD.CHAIN_NO ) VW LEFT OUTER JOIN ( SELECT TRANS_COMP AS CHAIN_NO , SUM(IN_AMT) AS CREDIT_AMT FROM TB_CREDIT_DEPOSIT_LIST WHERE BANK_IN_DATE = #{sch_resv_date} GROUP BY TRANS_COMP ) CR ON CR.CHAIN_NO = VW.CHAIN_NO STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = VW.CHAIN_NO
AND TC.CORP_CD = #{sch_corp_cd}
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_chain_nm}, '%')
WHERE 1= 1
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

/* DepositController.getDepositSummaryTotal */ SELECT SQL_CALC_FOUND_ROWS IFNULL(SUM(VW.TOT_CNT), 0) as tot_cnt , IFNULL(SUM(VW.CONF_CNT), 0) as CONF_CNT , IFNULL(SUM(CNCL_CNT), 0) as cncl_cnt , IFNULL(SUM(VW.TOT_AMT), 0) as tot_amt , IFNULL(SUM(VW.CONF_AMT), 0) as conf_amt , IFNULL(SUM(VW.CNCL_AMT), 0) as cncl_amt , IFNULL(SUM(VW.CARD_FEE_AMT), 0) as card_fee_amt , IFNULL(SUM(VW.CARD_RESV_AMT), 0) as card_resv_amt , IFNULL(SUM(VW.CARD_RESV_AMT_REMIT), 0) as card_resv_amt_remit , IFNULL(SUM(VW.DEPO_AMT), 0) as depo_amt , IFNULL(SUM(VW.DEPO_GAP_AMT_REMIT), 0) as depo_gap_amt_remit , IFNULL(SUM(VW.DEPO_GAP_AMT), 0) as depo_gap_amt , IFNULL(SUM(VW.REMIT_AMT), 0) as remit_amt , IFNULL(SUM(CR.CREDIT_AMT), 0) as credit_amt FROM ( SELECT WD.CHAIN_NO , SUM(TOT_CNT) AS TOT_CNT , SUM(CONF_CNT) AS CONF_CNT , SUM(CNCL_CNT) AS CNCL_CNT , SUM(CONF_AMT) AS CONF_AMT , SUM(CNCL_AMT) AS CNCL_AMT , SUM(TOT_SALES_AMT) AS TOT_AMT , SUM(CARD_FEE_AMT) AS CARD_FEE_AMT , SUM(CARD_RESV_AMT) AS CARD_RESV_AMT , SUM(CARD_RESV_AMT_REMIT) AS CARD_RESV_AMT_REMIT , SUM(DEPO_AMT) AS DEPO_AMT , SUM(DEPO_GAP_AMT) AS DEPO_GAP_AMT , SUM(DEPO_GAP_AMT_REMIT) AS DEPO_GAP_AMT_REMIT , SUM(SVC_FEE_AMT) AS SVC_FEE_AMT , SUM(CRD_FEE_AMT) AS CRD_FEE_AMT , SUM(WD_BASE_AMT) AS WD_BASE_AMT , SUM(REMIT_AMT) AS REMIT_AMT FROM VW_DEPOSIT_RESV_LIST WD WHERE WD.CARD_RESV_DATE = #{sch_resv_date} GROUP BY WD.CHAIN_NO ) VW LEFT OUTER JOIN ( SELECT TRANS_COMP AS CHAIN_NO , SUM(IN_AMT) AS CREDIT_AMT FROM TB_CREDIT_DEPOSIT_LIST WHERE BANK_IN_DATE = #{sch_resv_date} GROUP BY TRANS_COMP ) CR ON CR.CHAIN_NO = VW.CHAIN_NO STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = VW.CHAIN_NO
AND TC.CORP_CD = #{sch_corp_cd}
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_chain_nm}, '%')
WHERE 1= 1

/* DepositController.getDepoCardSummary */ SELECT SQL_CALC_FOUND_ROWS VW.CHAIN_NO as chain_no , TC.CHAIN_NM as chain_nm , TC.BIZ_NO as biz_no , VW.CARD_ACQ as card_acq , GET_CODE_NM('CARD_ACQ', VW.CARD_ACQ) as card_acq_nm , IFNULL(TOT_CNT, 0) as tot_cnt , IFNULL(CONF_CNT, 0) as CONF_CNT , IFNULL(CNCL_CNT, 0) as cncl_cnt , IFNULL(TOT_AMT, 0) as tot_amt , IFNULL(CONF_AMT, 0) as conf_amt , IFNULL(CNCL_AMT, 0) as cncl_amt , IFNULL(CARD_FEE_AMT, 0) as card_fee_amt , IFNULL(CARD_RESV_AMT, 0) as card_resv_amt , IFNULL(CARD_RESV_AMT_REMIT, 0) as card_resv_amt_remit , IFNULL(DEPO_AMT, 0) as depo_amt , IFNULL(DEPO_GAP_AMT_REMIT, 0) as depo_gap_amt_remit , IFNULL(DEPO_GAP_AMT, 0) as depo_gap_amt , IFNULL(REMIT_AMT, 0) as remit_amt FROM ( SELECT WD.CHAIN_NO, WD.CARD_ACQ , SUM(TOT_CNT) AS TOT_CNT , SUM(CONF_CNT) AS CONF_CNT , SUM(CNCL_CNT) AS CNCL_CNT , SUM(CONF_AMT) AS CONF_AMT , SUM(CNCL_AMT) AS CNCL_AMT , SUM(TOT_SALES_AMT) AS TOT_AMT , SUM(CARD_FEE_AMT) AS CARD_FEE_AMT , SUM(CARD_RESV_AMT) AS CARD_RESV_AMT , SUM(CARD_RESV_AMT_REMIT) AS CARD_RESV_AMT_REMIT , SUM(DEPO_AMT) AS DEPO_AMT , SUM(DEPO_GAP_AMT) AS DEPO_GAP_AMT , SUM(DEPO_GAP_AMT_REMIT) AS DEPO_GAP_AMT_REMIT , SUM(SVC_FEE_AMT) AS SVC_FEE_AMT , SUM(CRD_FEE_AMT) AS CRD_FEE_AMT , SUM(WD_BASE_AMT) AS WD_BASE_AMT , SUM(REMIT_AMT) AS REMIT_AMT FROM VW_DEPOSIT_RESV_LIST WD WHERE WD.CARD_RESV_DATE = #{sch_card_resv_date} AND WD.CHAIN_NO = #{sch_card_chain_no} GROUP BY WD.CHAIN_NO , WD.CARD_ACQ ) VW JOIN TB_CHAIN TC ON TC.CHAIN_NO = VW.CHAIN_NO WHERE 1= 1
ORDER BY ${orderBy}
ORDER BY 1

/*  DepositController.getSearchConfDate  */
SELECT 	MIN(DATE_FORMAT(CONF_DTTM, '%Y-%m-%d'))  AS conf_sdt
		, MAX(DATE_FORMAT(CONF_DTTM, '%Y-%m-%d')) AS conf_edt
FROM  	TB_WITHDRAW
WHERE  CARD_RESV_DATE 	= #{sch_resv_date} 

  	AND  CHAIN_NO      	= #{select_chain_no} 

  AND  USE_YN        	= 'Y'

/* DepositController.getDepoResvList */ SELECT SQL_CALC_FOUND_ROWS WD.WD_NO AS wd_no , WD.WD_STATUS AS wd_status , GET_CODE_NM('WD_STATUS', WD.WD_STATUS) AS wd_status_nm , WD.WORK_DATE AS work_date , WD.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , WD.DOCU_SEQ AS docu_seq , WD.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', WD.CARD_ACQ) AS card_acq_nm , VD.CARD_NO AS card_no , WD.CARD_REG_NO AS card_reg_no , VD.CARD_TYPE AS card_type , GET_CODE_NM('CARD_TYPE', VD.CARD_TYPE) AS card_type_nm , WD.CONF_NO AS conf_no , WD.CONF_GB AS conf_gb , CASE WHEN WD.CONF_GB = 'A' THEN '승인' ELSE '취소' END AS conf_gb_nm , WD.CONF_DTTM AS conf_dttm , WD.CONF_AMT AS conf_amt , WD.CARD_BUY_FLAG AS card_buy_flag , WD.CARD_BUY_DATE AS card_buy_date , WD.CARD_FEE_RATE AS fard_fee_rate , WD.CARD_FEE_AMT AS card_fee_amt , IFNULL(WD.CARD_RESV_AMT, 0) AS card_resv_amt , IFNULL(WD.CARD_RESV_DAY, 0) AS card_resv_day , IFNULL(WD.CARD_RESV_DATE, '') AS card_resv_date , IFNULL(WD.SVC_FEE_RATE, 0) AS svc_fee_rate , IFNULL(WD.SVC_FEE_AMT, 0) AS svc_fee_amt , IFNULL(WD.WD_BASE_AMT, 0) AS wd_base_amt , IFNULL(WD.CRD_FEE_RATE, 0) AS crd_fee_rate , IFNULL(WD.CRD_FEE_AMT, 0) AS crd_fee_amt , IFNULL(WD.SUB_FLAG, 'N') AS sub_flag , IFNULL(WD.REMIT_AMT, 0) AS remit_amt , IFNULL(WD.REMIT_NO, '') AS remit_seq , IFNULL(WD.REMIT_DT, '') AS remit_dt , IFNULL(WD.DEPOSIT_FLAG, '') AS deposit_flag , IFNULL(WD.BANK_IN_DATE, '') AS bank_in_date , IFNULL(WD.CARDSALES_FG, '') AS cardsales_fg , WD.USE_YN AS use_yn , IFNULL(WD.WD_MEMO, '') AS wd_memo , WD.CLOSE_YN AS close_yn , DATE_FORMAT(IFNULL(WD.UPT_DTTM, WD.ENT_DTTM), '%Y-%m-%d %H:%i:%s') AS upt_dttm , DATE_FORMAT(IFNULL(WD.ENT_USER_ID, WD.UPT_USER_ID), '%Y-%m-%d %H:%i:%s') AS upt_user_id FROM TB_WITHDRAW WD JOIN TB_VAN_DOCU VD ON VD.DOCU_SEQ = WD.DOCU_SEQ JOIN TB_CHAIN TC ON TC.CHAIN_NO = WD.CHAIN_NO WHERE WD.DEPOSIT_DTTM IS NULL AND WD.DEPOSIT_FLAG = 'N' AND WD.CARD_RESV_DATE = #{sch_list_resv_date} AND WD.CHAIN_NO = #{sch_list_chain_no} AND WD.USE_YN = 'Y'
AND FIND_IN_SET(WD.CARD_ACQ, #{sch_list_card_acq})
AND WD.CONF_DTTM >= CONCAT(#{sch_list_conf_sdt}, " 00:00:00")
AND WD.CONF_DTTM <= CONCAT(#{sch_list_conf_edt}, " 23:59:59")
AND WD.CONF_NO = #{sch_list_gb_conf_val}
AND WD.CARD_NO = #{sch_list_gb_conf_val}
AND WD.CONF_AMT = #{sch_list_gb_conf_val}
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

mapper.update:
{ 
	CALL PRC_DEPOSIT_CHANGE_RESV_DATE(
			#{wdNo, mode=IN, jdbcType=VARCHAR} 
			, #{changeResvDate, mode=IN, jdbcType=VARCHAR}
			, #{userId, mode=IN, jdbcType=VARCHAR}
			, #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
			, #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
	) 
}

{ 
	CALL PRC_DEPOSIT_ADJUST(
		#{resvDate, mode=IN, jdbcType=VARCHAR} 
		, #{chainNo, mode=IN, jdbcType=VARCHAR}
		, #{cardAcq, mode=IN, jdbcType=VARCHAR}
		, #{userId, mode=IN, jdbcType=VARCHAR}
		, #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
		, #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
	) 
}

{ 
	CALL PRC_DEPOSIT_DATA_DELETE(
		#{depositNo, mode=IN, jdbcType=VARCHAR}  
		, #{corpGb, mode=IN, jdbcType=VARCHAR}
		, #{userId, mode=IN, jdbcType=VARCHAR}
		, #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
		, #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
	) 
}

mapper.select:
/* DepositController.getDepoAdjustSummary */ SELECT SQL_CALC_FOUND_ROWS DA.CHAIN_NO as chain_no , TC.CHAIN_NM as chain_nm , TC.BIZ_NO as biz_no , IFNULL(SUM(TOT_SALES_AMT), 0) as tot_sales_amt , IFNULL(SUM(CARD_FEE_AMT), 0) as card_fee_amt , IFNULL(SUM(ORG_CARD_RESV_AMT), 0) as org_card_resv_amt , IFNULL(SUM(CARD_RESV_AMT_REMIT), 0) as card_resv_amt_remit , IFNULL(SUM(NEW_CARD_RESV_AMT), 0) as new_card_resv_amt , IFNULL(SUM(RESV_BASE_AMT), 0) as resv_base_amt , IFNULL(SUM(RESV_SVC_AMT), 0) as resv_svc_amt , IFNULL(SUM(RESV_CRD_AMT), 0) as resv_crd_amt , IFNULL(SUM(BANK_IN_AMT), 0) as bank_in_amt , IFNULL(SUM(DEPO_GAP_AMT), 0) as depo_gap_amt , IFNULL(SUM(DEPO_GAP_AMT_REMIT), 0) as depo_gap_amt_remit , IFNULL(SUM(DEPO_GAP_AMT), 0) as depo_gap_amt , IFNULL(SUM(BANK_IN_BASE_AMT), 0) as bank_in_base_amt , IFNULL(SUM(BANK_IN_SVC_AMT), 0) as bank_in_svc_amt , IFNULL(SUM(BANK_IN_CRD_AMT), 0) as bank_in_crd_amt , IFNULL(SUM(EXC_AMT), 0) as exc_amt , IFNULL(SUM(EXC_CARD_AMT), 0) as exc_card_amt , IFNULL(SUM(EXC_CRD_AMT), 0) as exc_crd_amt , IFNULL(SUM(SUB_CARD_AMT), 0) as sub_card_amt , IFNULL(SUM(SUB_AMT), 0) as sub_amt , IFNULL(SUM(SUB_BASE_AMT), 0) as sub_base_amt , IFNULL(SUM(SUB_SVC_AMT), 0) as sub_svc_amt , IFNULL(SUM(SUB_CRD_AMT), 0) as sub_crd_amt FROM TB_DEPOSIT_ADJUST DA JOIN TB_CHAIN TC ON TC.CHAIN_NO = DA.CHAIN_NO
AND TC.CORP_CD = #{sch_corp_cd}
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_chain_nm}, '%')
WHERE DA.ADJUST_DATE = #{sch_adjust_date} GROUP BY DA.CHAIN_NO, TC.CHAIN_NM, TC.BIZ_NO
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

/* DepositController.getDepoAdjustSummTotal */ SELECT IFNULL(SUM(TOT_SALES_AMT), 0) as tot_sales_amt , IFNULL(SUM(CARD_FEE_AMT), 0) as card_fee_amt , IFNULL(SUM(ORG_CARD_RESV_AMT), 0) as org_card_resv_amt , IFNULL(SUM(CARD_RESV_AMT_REMIT), 0) as card_resv_amt_remit , IFNULL(SUM(NEW_CARD_RESV_AMT), 0) as new_card_resv_amt , IFNULL(SUM(RESV_BASE_AMT), 0) as resv_base_amt , IFNULL(SUM(RESV_SVC_AMT), 0) as resv_svc_amt , IFNULL(SUM(RESV_CRD_AMT), 0) as resv_crd_amt , IFNULL(SUM(BANK_IN_AMT), 0) as bank_in_amt , IFNULL(SUM(DEPO_GAP_AMT), 0) as depo_gap_amt , IFNULL(SUM(DEPO_GAP_AMT_REMIT), 0) as depo_gap_amt_remit , IFNULL(SUM(BANK_IN_BASE_AMT), 0) as bank_in_base_amt , IFNULL(SUM(BANK_IN_SVC_AMT), 0) as bank_in_svc_amt , IFNULL(SUM(BANK_IN_CRD_AMT), 0) as bank_in_crd_amt , IFNULL(SUM(EXC_AMT), 0) as exc_amt , IFNULL(SUM(EXC_CARD_AMT), 0) as exc_card_amt , IFNULL(SUM(EXC_CRD_AMT), 0) as exc_crd_amt , IFNULL(SUM(SUB_CARD_AMT), 0) as sub_card_amt , IFNULL(SUM(SUB_AMT), 0) as sub_amt , IFNULL(SUM(SUB_BASE_AMT), 0) as sub_base_amt , IFNULL(SUM(SUB_SVC_AMT), 0) as sub_svc_amt , IFNULL(SUM(SUB_CRD_AMT), 0) as sub_crd_amt FROM TB_DEPOSIT_ADJUST DA JOIN TB_CHAIN TC ON TC.CHAIN_NO = DA.CHAIN_NO
AND TC.CORP_CD = #{sch_corp_cd}
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_chain_nm}, '%')
WHERE ADJUST_DATE = #{sch_adjust_date}

/* DepositController.getDepoAdjustCardSummary */ SELECT SQL_CALC_FOUND_ROWS DA.CHAIN_NO as chain_no , TC.CHAIN_NM as chain_nm , TC.BIZ_NO as biz_no , DA.CARD_ACQ as card_acq , GET_CODE_NM('CARD_ACQ', DA.CARD_ACQ) as card_acq_nm , IFNULL(SUM(TOT_SALES_AMT), 0) as tot_sales_amt , IFNULL(SUM(CARD_FEE_AMT), 0) as card_fee_amt , IFNULL(SUM(ORG_CARD_RESV_AMT), 0) as org_card_resv_amt , IFNULL(SUM(CARD_RESV_AMT_REMIT), 0) as card_resv_amt_remit , IFNULL(SUM(NEW_CARD_RESV_AMT), 0) as new_card_resv_amt , IFNULL(SUM(RESV_BASE_AMT), 0) as resv_base_amt , IFNULL(SUM(RESV_SVC_AMT), 0) as resv_svc_amt , IFNULL(SUM(RESV_CRD_AMT), 0) as resv_crd_amt , IFNULL(SUM(BANK_IN_AMT), 0) as bank_in_amt , IFNULL(SUM(DEPO_GAP_AMT), 0) as depo_gap_amt , IFNULL(SUM(depo_gap_amt_remit), 0) as depo_gap_amt_remit , IFNULL(SUM(BANK_IN_BASE_AMT), 0) as bank_in_base_amt , IFNULL(SUM(BANK_IN_SVC_AMT), 0) as bank_in_svc_amt , IFNULL(SUM(BANK_IN_CRD_AMT), 0) as bank_in_crd_amt , IFNULL(SUM(EXC_AMT), 0) as exc_amt , IFNULL(SUM(EXC_CARD_AMT), 0) as exc_card_amt , IFNULL(SUM(EXC_CRD_AMT), 0) as exc_crd_amt , IFNULL(SUM(SUB_CARD_AMT), 0) as sub_card_amt , IFNULL(SUM(SUB_AMT), 0) as sub_amt , IFNULL(SUM(SUB_BASE_AMT), 0) as sub_base_amt , IFNULL(SUM(SUB_SVC_AMT), 0) as sub_svc_amt , IFNULL(SUM(SUB_CRD_AMT), 0) as sub_crd_amt FROM TB_DEPOSIT_ADJUST DA JOIN TB_CHAIN TC ON TC.CHAIN_NO = DA.CHAIN_NO WHERE DA.ADJUST_DATE = #{sch_card_adjust_date} AND DA.CHAIN_NO = #{sch_card_chain_no} GROUP BY DA.CHAIN_NO, TC.CHAIN_NM, TC.BIZ_NO, DA.CARD_ACQ, GET_CODE_NM('CARD_ACQ', DA.CARD_ACQ)
ORDER BY ${orderBy}
ORDER BY 1

/* DepositController.getDepoAdjustList */ SELECT SQL_CALC_FOUND_ROWS WD.WD_NO AS wd_no , WD.WD_STATUS AS wd_status , GET_CODE_NM('WD_STATUS', WD.WD_STATUS) AS wd_status_nm , WD.WORK_DATE AS work_date , WD.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , WD.DOCU_SEQ AS docu_seq , WD.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', WD.CARD_ACQ) AS card_acq_nm , VD.CARD_NO AS card_no , WD.CARD_REG_NO AS card_reg_no , VD.CARD_TYPE AS card_type , GET_CODE_NM('CARD_TYPE', VD.CARD_TYPE) AS card_type_nm , WD.CONF_NO AS conf_no , WD.CONF_GB AS conf_gb , CASE WHEN WD.CONF_GB = 'A' THEN '승인' ELSE '취소' END AS conf_gb_nm , WD.CONF_DTTM AS conf_dttm , WD.CONF_AMT AS conf_amt , WD.CARD_BUY_FLAG AS card_buy_flag , WD.CARD_BUY_DATE AS card_buy_date , WD.CARD_FEE_RATE AS fard_fee_rate , WD.CARD_FEE_AMT AS card_fee_amt , IFNULL(WD.CARD_RESV_AMT, 0) AS card_resv_amt , IFNULL(WD.CARD_RESV_DAY, 0) AS card_resv_day , IFNULL(WD.CARD_RESV_DATE, '') AS card_resv_date , IFNULL(WD.SVC_FEE_RATE, 0) AS svc_fee_rate , IFNULL(WD.SVC_FEE_AMT, 0) AS svc_fee_amt , IFNULL(WD.WD_BASE_AMT, 0) AS wd_base_amt , IFNULL(WD.CRD_FEE_RATE, 0) AS crd_fee_rate , IFNULL(WD.CRD_FEE_AMT, 0) AS crd_fee_amt , IFNULL(WD.SUB_FLAG, 'N') AS sub_flag , IFNULL(WD.REMIT_AMT, 0) AS remit_amt , IFNULL(WD.REMIT_NO, '') AS remit_seq , IFNULL(WD.REMIT_DT, '') AS remit_dt , IFNULL(WD.DEPOSIT_FLAG, '') AS deposit_flag , IFNULL(WD.BANK_IN_DATE, '') AS bank_in_date , IFNULL(WD.CARDSALES_FG, '') AS cardsales_fg , WD.USE_YN AS use_yn , IFNULL(WD.WD_MEMO, '') AS wd_memo , WD.CLOSE_YN AS close_yn , DATE_FORMAT(IFNULL(WD.UPT_DTTM, WD.ENT_DTTM), '%Y-%m-%d %H:%i:%s') AS upt_dttm , DATE_FORMAT(IFNULL(WD.ENT_USER_ID, WD.UPT_USER_ID), '%Y-%m-%d %H:%i:%s') AS upt_user_id FROM TB_WITHDRAW WD STRAIGHT_JOIN TB_VAN_DOCU VD ON VD.DOCU_SEQ = WD.DOCU_SEQ STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = WD.CHAIN_NO WHERE 1 = 1 AND WD.DEPOSIT_DTTM BETWEEN DATE(#{sch_list_adjust_date}) AND DATE_ADD(DATE(#{sch_list_adjust_date}), INTERVAL 1 DAY) AND WD.DEPOSIT_FLAG = 'Y' AND WD.CHAIN_NO = #{sch_list_chain_no} AND WD.USE_YN = 'Y'
AND FIND_IN_SET(WD.CARD_ACQ, #{sch_list_card_acq})
AND WD.CONF_DTTM >= CONCAT(#{sch_list_conf_sdt}, ' 00:00:00')
AND WD.CONF_DTTM <= CONCAT(#{sch_list_conf_edt}, ' 23:59:59')
AND WD.CONF_NO = #{sch_list_gb_conf_val}
AND WD.CARD_NO = #{sch_list_gb_conf_val}
AND WD.CONF_AMT = #{sch_list_gb_conf_val}
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

/* DepositController.getDepositConfDate */ SELECT MIN(DATE_FORMAT(CONF_DTTM, '%Y-%m-%d')) AS conf_sdt , MAX(DATE_FORMAT(CONF_DTTM, '%Y-%m-%d')) AS conf_edt FROM TB_WITHDRAW WHERE DEPOSIT_DTTM BETWEEN DATE(#{sch_adjust_date}) AND DATE_ADD(DATE(#{sch_adjust_date}), INTERVAL 1 DAY)
AND CHAIN_NO = #{sch_chain_no}
AND USE_YN = 'Y'

/* DepositController.getChainDepositList */ SELECT SQL_CALC_FOUND_ROWS DL.DEPOSIT_NO AS deposit_no , DL.CHAIN_NO AS chain_no , DL.GUBUN AS gubun , CASE WHEN DL.BANK_IN_DATE = DL.BANK_IN_REAL_DATE THEN CONCAT(BANK_IN_DATE, ' ', DL.BANK_IN_TM) ELSE CONCAT(BANK_IN_DATE, ' ', DL.BANK_IN_TM, ' (', DL.BANK_IN_REAL_DATE, ')') END bank_in_dttm , DL.BANK_IN_DATE AS bank_in_date , DL.BANK_IN_TM AS bank_in_tm , DL.CARD_ACQ AS send_corp , GET_CODE_NM('CARD_ACQ', DL.CARD_ACQ) AS send_corp_nm , DL.BANK_CD AS bank_cd , GET_CODE_NM('BANK_CD', DL.BANK_CD) AS bank_cd_nm , DL.IN_AMT AS in_amt , IFNULL(SD.REMARK, '') AS remark FROM TB_CHAIN_DEPOSIT_LIST DL STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = DL.CHAIN_NO LEFT OUTER JOIN ( SELECT 'SCRAP' GB, SEQ, REMARK FROM TB_SCRAP_BANK_DATA UNION ALL SELECT 'KSOL', SEQ, CARD_CORP_NM FROM TB_SCRAP_KSOLUTION_DATA ) SD ON SD.SEQ = DL.UPLOAD_SEQ_NO AND SD.GB = DL.GUBUN WHERE DL.BANK_IN_DATE = #{sch_date} AND DL.CHAIN_NO = #{sch_chain_no} AND DL.IN_AMT > 0
ORDER BY ${orderBy}
ORDER BY 1

/* DepositController.getChainMnulDepositList */ SELECT SQL_CALC_FOUND_ROWS DL.DEPOSIT_NO AS deposit_no , DL.CHAIN_NO AS chain_no , DL.GUBUN AS gubun , CONCAT(BANK_IN_DATE, ' ', DL.BANK_IN_TM) AS bank_in_dttm , DL.BANK_IN_DATE AS bank_in_date , DL.BANK_IN_TM AS bank_in_tm , DL.CARD_ACQ AS send_corp , GET_CODE_NM('CARD_ACQ', DL.CARD_ACQ) AS send_corp_nm , DL.BANK_CD AS bank_cd , GET_CODE_NM('BANK_CD', DL.BANK_CD) AS bank_cd_nm , DL.IN_AMT AS in_amt , DL.ADJUST_YN AS adjust_yn -- , DL.ENT_USER_ID AS ent_user_id FROM TB_CHAIN_DEPOSIT_LIST DL STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = DL.CHAIN_NO WHERE DL.GUBUN = 'MNUL' AND DL.BANK_IN_DATE = #{sch_date} AND DL.CHAIN_NO = #{sch_chain_no} AND DL.IN_AMT > 0
ORDER BY ${orderBy}
ORDER BY 1

/*  DepositController.getChainDepoInfo  */
SELECT  CORP_CD, ABANK_CD, ABANK_ACCOUNT_NO  
  FROM TB_CHAIN TC
 WHERE  TC.CHAIN_NO 	= #{chain_no}

mapper.insert:
		/*  DepositController.insertMnulChainDeposit  */
        INSERT INTO TB_CHAIN_DEPOSIT_LIST (
            DEPOSIT_NO		, CHAIN_NO		, BANK_CD			, BANK_IN_DATE		, BANK_IN_TM
			, CARD_ACQ		, IN_AMT		, OUT_AMT			, REMAIN_AMT		, BANK_IN_REAL_DATE
			, ADJUST_YN		, GUBUN			, ENT_USER_ID		, ENT_DTTM
        ) VALUES (
            #{deposit_no}	, #{chain_no} 	, #{bank_cd} 		, DATE_FORMAT(NOW(), '%Y-%m-%d')	, DATE_FORMAT(NOW(), '%H:%i:%s') 
			, #{card_acq} 	, #{in_amt} 	, 0 				, #{remain_amt} 	, #{bank_in_real_date}
			,'N'  			, #{gubun}		, #{ent_user_id}	, NOW()
        )

		/*  DepositController.insertMnulCreitDeposit  */
        INSERT INTO TB_CREDIT_DEPOSIT_LIST (
            DEPOSIT_NO			, CORP_CD			, BANK_IN_DATE		, BANK_IN_TM	
			, IO_GB				, IN_AMT			, TRANS_CODE		, TRANS_COMP	
			, UPLOAD_GUBUN		, BK_DIV			, ENT_DTTM			, ENT_USER_ID
        ) VALUES (
            GET_JOB_SEQ('TB_CREDIT_DEPOSIT_LIST', 'DEPOSIT_NO') 
			, #{corp_cd} 		, DATE_FORMAT(NOW(), '%Y-%m-%d')		, DATE_FORMAT(NOW(), '%H:%i:%s')
			, 'I'				, #{in_amt} 		, '0000'			, #{chain_no}	
			, 'MNUL'			, #{chain_deposit_no}					, NOW()				, #{ent_user_id}
		)

mapper.delete:
		/*  DepositController.deleteMnulChainDeposit  */
        DELETE FROM TB_CHAIN_DEPOSIT_LIST
		 WHERE  BANK_IN_DATE	= #{bank_in_date} 
		   AND  CHAIN_NO 		= #{chain_no} 
		   AND  GUBUN			= 'MNUL'
		   AND 	ADJUST_YN 		= 'N'
		   AND  DEPOSIT_NO		= #{deposit_no}

		/*  DepositController.deletetMnulCreitDeposit  */
        DELETE TB_CREDIT_DEPOSIT_LIST 
		 WHERE  BANK_IN_DATE	= #{bank_in_date} 
		   AND  TRANS_COMP 		= #{chain_no} 
		   AND  UPLOAD_GUBUN	= 'MNUL' 
		   AND  BK_DIV			= #(chain_deposit_no}

mapper.select:
/*  DepositController.getChainDepositTotal  */
SELECT 	IFNULL(SUM(DL.IN_AMT), 0) 	AS in_amt
  FROM 	TB_CHAIN_DEPOSIT_LIST DL
	   	STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = DL.CHAIN_NO 
 WHERE	DL.BANK_IN_DATE	= #{sch_date} 
   AND  DL.CHAIN_NO 	= #{sch_chain_no}		
   AND	DL.IN_AMT > 0

/* DepositController.getCreditDepositList */ SELECT SQL_CALC_FOUND_ROWS DL.DEPOSIT_NO AS deposit_no , DL.TRANS_COMP AS chain_no , DL.UPLOAD_GUBUN AS gubun , CONCAT(BANK_IN_DATE, ' ', DL.BANK_IN_TM) AS bank_in_dttm , DL.BANK_IN_DATE AS bank_in_date , DL.BANK_IN_TM AS bank_in_tm , DL.TRANS_COMP AS send_corp , TC.CHAIN_NM AS send_corp_nm , CO.BANK_CD AS bank_cd , GET_CODE_NM('BANK_CD', CO.BANK_CD) AS bank_cd_nm , DL.IN_AMT AS in_amt , IFNULL(DL.BK_JUKYO, '') AS remark FROM TB_CREDIT_DEPOSIT_LIST DL STRAIGHT_JOIN TB_CORP CO ON CO.CORP_CD = DL.CORP_CD STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = DL.TRANS_COMP WHERE DL.BANK_IN_DATE = #{sch_date} AND DL.TRANS_COMP = #{sch_chain_no} AND DL.IN_AMT > 0
ORDER BY ${orderBy}
ORDER BY 1

/*  DepositController.getCreditDepositTotal  */
SELECT 	IFNULL(SUM(DL.IN_AMT), 0) 	AS in_amt
  FROM 	TB_CREDIT_DEPOSIT_LIST DL
	   	JOIN TB_CORP CO 	ON CO.CORP_CD = DL.CORP_CD  			   	
	   	JOIN TB_CHAIN TC 	ON TC.CHAIN_NO = DL.TRANS_COMP
 WHERE	DL.BANK_IN_DATE	= #{sch_date} 
   AND  DL.TRANS_COMP 	= #{sch_chain_no}
   AND	DL.IN_AMT > 0


/*  DepositController.getCorpAccountNo  */
SELECT  ACCOUNT_NO
  FROM 	(
			SELECT  ABANK_ACCOUNT_NO 	AS ACCOUNT_NO
			FROM 	TB_CHAIN
			WHERE 	CHAIN_NO = #{corpCd}
			  AND   'CH' = #{corpType}
			UNION 	ALL  
			SELECT 	ACCOUNT_NO 
			FROM 	TB_CORP
			WHERE 	CORP_CD = #{corpCd}
			  AND   'LN' = #{corpType}
  		) VT
LIMIT	1

mapper.update:
/*  DepositController.callProcDepoAdjustCancel  */
{ 
	CALL PRC_DEPOSIT_ADJUST_CANCEL(
		#{adjustDate, mode=IN, jdbcType=VARCHAR} 
		, #{chainNo, mode=IN, jdbcType=VARCHAR}
		, #{cardAcq, mode=IN, jdbcType=VARCHAR}
		, #{userId, mode=IN, jdbcType=VARCHAR}
		, #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
		, #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
	) 
}

mapper.insert:
/*  DepositController.excelUploadBankData  */
INSERT INTO TB_SCRAP_BANK_DATA
(
	CHAIN_NO		, BANK_CD		, ACCOUNT_NO	, TRADE_DTTM		, TRADE_GB	
	, REMARK		, OUT_AMT		, IN_AMT		, REMAIN_AMT		, CENTER_NM
	, STATUS 		, ENT_DTTM		, INSERT_GB
) VALUES (
	#{corpCd}		, #{bankCd}		, #{accountNo}	, #{tradeDttm}		, #{tradeGb}				 
	, #{remark}		, #{outAmt}		, #{inAmt}		, #{remainAmt}		, #{centerNm}
	, 'S'			, NOW()			, 'EXCEL'
)

mapper.update:
/*  DepositController.callProcTransDepositBatch  */
{ 
	CALL PRC_SCRAP_TRANS_BANK_DEPOSIT(
		  #{chainNo, mode=IN, jdbcType=VARCHAR}
		, #{userId, mode=IN, jdbcType=VARCHAR}
		, #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
		, #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
	) 
}
