mapper.select:
SELECT SQL_CALC_FOUND_ROWS TC.CHAIN_NO as chain_no , TC.CHAIN_NM AS chain_nm , TC.BIZ_NO as biz_no , TU.USER_NM AS ceo_nm , TC.CORP_CD as corp_cd , CO.CORP_NM as corp_nm , TC.CRD_FEE_RATE as int_rate , TC.CRD_DELAY_RATE as delay_rate , TC.TOT_LIMIT_AMT as tot_limit_amt , TC.TOT_USE_AMT as tot_use_amt , IFNULL(LM.MIN_CONT_DT, DATE_FORMAT(NOW(), '%Y-%m-%d')) as min_cont_dt , IFNULL(LM.MAX_CONT_DT, DATE_FORMAT(NOW(), '%Y-%m-%d')) as max_cont_dt , IFNULL(LM.BIZ_LOAN_AMT, 0) AS biz_loan_amt , IFNULL(LM.SPOT_LOAN_AMT, 0) AS spot_loan_amt , IFNULL(LM.ETC_LOAN_AMT, 0) AS etc_loan_amt , IFNULL(LM.TOT_LOAN_AMT, 0) AS tot_loan_amt , IFNULL(LM.RECV_AMT, 0) AS recv_amt , IFNULL(LM.REMAIN_AMT, 0) AS remain_amt , TC.SVC_STAT as svc_stat , GET_CODE_NM('SVC_STAT', TC.SVC_STAT) AS svc_stat_nm , TC.REMIT_STAT as remit_stat , GET_CODE_NM('REMIT_STAT', TC.REMIT_STAT) AS remit_stat_nm FROM TB_CHAIN TC LEFT OUTER JOIN ( SELECT M.CHAIN_NO , MIN(M.CONT_DT) AS MIN_CONT_DT , MAX(M.CONT_DT) AS MAX_CONT_DT , IFNULL(SUM(CASE WHEN M.LOAN_TYPE = 'L01001' THEN M.PRINC_AMT ELSE NULL END), 0) AS BIZ_LOAN_AMT , IFNULL(SUM(CASE WHEN M.LOAN_TYPE = 'L02001' THEN M.PRINC_AMT ELSE NULL END), 0) AS SPOT_LOAN_AMT , IFNULL(SUM(CASE WHEN M.LOAN_TYPE IN('L01001', 'L02001') THEN NULL ELSE M.PRINC_AMT END), 0) AS ETC_LOAN_AMT , IFNULL(SUM(M.PRINC_AMT), 0) AS TOT_LOAN_AMT , IFNULL(SUM(SC.RECV_AMT), 0) AS RECV_AMT , IFNULL(SUM(SC.REMAIN_AMT), 0) AS REMAIN_AMT FROM TB_LOAN_MST M LEFT OUTER JOIN ( SELECT LOAN_NO , SUM(RECV_PRINC_AMT) AS RECV_AMT , SUM(REMAIN_PRINC_AMT) AS REMAIN_AMT FROM TB_LOAN_REPAY_SCHEDULE WHERE USE_YN = 'Y' GROUP BY LOAN_NO ) SC ON SC.LOAN_NO = M.LOAN_NO WHERE M.USE_YN = 'Y'
AND M.LOAN_STATUS = '01'
GROUP BY M.CHAIN_NO ) LM ON TC.CHAIN_NO = LM.CHAIN_NO JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID JOIN TB_CORP CO ON CO.CORP_CD = TC.CORP_CD AND CO.CORP_TYPE = 'LN'
WHERE TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('R', 'O')
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_chain_nm}, '%')
AND TC.CORP_CD = #{sch_corp_cd}
AND TC.CORP_CD = #{userCorpCd}
AND 1 = 1
AND 1 = 0
ORDER BY ${sidx} ${sord}
ORDER BY 1
LIMIT ${start}, ${end}

SELECT SQL_CALC_FOUND_ROWS LM.CHAIN_NO AS chain_no , LM.CORP_CD AS corp_cd , CO.CORP_NM AS corp_nm , TC.BIZ_NO AS biz_no , TU.USER_NM AS ceo_nm , LM.LOAN_NO AS loan_no , LM.LOAN_STATUS AS loan_status , GET_CODE_NM('LOAN_STATUS', LM.LOAN_STATUS) AS loan_status_nm , LM.LOAN_TYPE AS loan_type , GET_CODE_NM('LOAN_TYPE', LM.LOAN_TYPE) AS loan_type_nm , LM.SUB_CODE AS sub_code , LM.CONT_DT AS cont_dt , LM.PRINC_AMT AS princ_amt , LM.INT_RATE AS int_rate , LM.LOAN_SDT AS loan_sdt , LM.LOAN_DAY AS loan_day , LM.LOAN_EDT AS loan_edt , IFNULL(LM.LOAN_EXPR_DT, '') AS loan_expr_dt , LM.INT_AMT AS int_amt , LM.TOT_LOAN_AMT AS tot_loan_amt , IFNULL(SC.SC_CNT, 0) AS sc_cnt , IFNULL(SC.RECV_CNT, 0) AS recv_cnt , IFNULL(SC.UNRECV_CNT, 0) AS unrecv_cnt , IFNULL(SC.RECV_PRINC_AMT, 0) AS recv_princ_amt , IFNULL(SC.RECV_INT_AMT, 0) AS recv_int_amt , IFNULL(SC.RECV_TOT_AMT, 0) AS recv_tot_amt , IFNULL(SC.REMAIN_PRINC_AMT, 0) AS remain_princ_amt , IFNULL(SC.REMAIN_INT_AMT, 0) AS remain_int_amt , IFNULL(SC.REMAIN_TOT_AMT, 0) AS remain_tot_amt , IFNULL(SC.PRE_PRINC_AMT, 0) AS pre_princ_amt , IFNULL(SC.PRE_INC_AMT, 0) AS pre_int_amt , IFNULL(SC.POST_PRINC_AMT, 0) AS post_princ_amt , IFNULL(SC.POST_INT_AMT, 0) AS post_int_amt , 0 AS prepay_post_int_amt , LM.DELAY_RATE AS delay_rate , CASE WHEN IFNULL(DLY.DELAY_CNT, 0) > 0 THEN '연체' ELSE '정상' END AS delay_yn , CASE WHEN LM.DELAY_YN = 'Y' THEN '연체' ELSE '정상' END AS delay_yn2 , IFNULL(DLY.DELAY_CNT, 0) AS delay_cnt , IFNULL(DLY.DELAY_INT_AMT, 0) AS delay_int_amt , IFNULL(SC.PRE_PRINC_AMT, 0) + IFNULL(SC.PRE_INC_AMT, 0) + IFNULL(SC.POST_PRINC_AMT, 0) + IFNULL(DLY.DELAY_INT_AMT, 0) AS prepay_tot_amt , DATE_FORMAT(IFNULL(LM.UPT_DTTM, LM.ENT_DTTM), '%Y-%m-%d %H:%i:%s') AS upt_dttm , IFNULL(LM.UPT_USER_ID, LM.ENT_USER_ID ) AS upt_user_id FROM TB_LOAN_MST LM LEFT OUTER JOIN ( SELECT LOAN_NO , COUNT(1) AS SC_CNT , SUM(CASE WHEN RECV_YN = 'Y' THEN 1 ELSE NULL END) AS RECV_CNT , SUM(CASE WHEN RECV_YN = 'Y' THEN NULL ELSE 1 END) AS UNRECV_CNT , SUM(RECV_PRINC_AMT) AS RECV_PRINC_AMT , SUM(RECV_INT_AMT) AS RECV_INT_AMT , SUM(RECV_TOT_AMT) AS RECV_TOT_AMT , SUM(REMAIN_PRINC_AMT) AS REMAIN_PRINC_AMT , SUM(REMAIN_INT_AMT) AS REMAIN_INT_AMT , SUM(REMAIN_TOT_AMT) AS REMAIN_TOT_AMT , SUM(CASE WHEN SC_DATE >= DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_PRINC_AMT ELSE NULL END) AS PRE_PRINC_AMT , SUM(CASE WHEN SC_DATE >= DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_INT_AMT ELSE NULL END) AS PRE_INC_AMT , SUM(CASE WHEN SC_DATE < DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_PRINC_AMT ELSE NULL END) AS POST_PRINC_AMT , SUM(CASE WHEN SC_DATE < DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_INT_AMT ELSE NULL END) AS POST_INT_AMT FROM TB_LOAN_REPAY_SCHEDULE WHERE USE_YN = 'Y' GROUP BY LOAN_NO ) SC ON SC.LOAN_NO = LM.LOAN_NO LEFT OUTER JOIN ( SELECT LOAN_NO , COUNT(DISTINCT SC_SEQ ) AS DELAY_CNT , SUM(REMAIN_INT_AMT) AS DELAY_INT_AMT FROM TB_LOAN_REPAY_DELAY WHERE USE_YN = 'Y' AND RECV_YN = 'N' GROUP BY LOAN_NO ) DLY ON DLY.LOAN_NO = LM.LOAN_NO JOIN TB_CHAIN TC ON TC.CHAIN_NO = LM.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('R', 'O') JOIN TB_CORP CO ON CO.CORP_CD = LM.CORP_CD JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID WHERE LM.USE_YN = 'Y' AND LM.CHAIN_NO = #{sch_chain_no}
AND LM.CONT_DT >= #{sch_cont_sdt}
AND LM.CONT_DT <= #{sch_cont_edt}
AND LM.LOAN_STATUS = #{sch_loan_status}
AND LM.LOAN_TYPE = #{sch_loan_type}
ORDER BY ${sidx} ${sord}
ORDER BY 1
LIMIT ${start}, ${end}

SELECT IFNULL(SUM(LM.PRINC_AMT), 0) AS princ_amt , IFNULL(SUM(LM.INT_AMT), 0) AS int_amt , IFNULL(SUM(LM.TOT_LOAN_AMT), 0) AS tot_loan_amt , IFNULL(SUM(SC.RECV_PRINC_AMT), 0) AS recv_princ_amt , IFNULL(SUM(SC.REMAIN_PRINC_AMT), 0) AS remain_princ_amt , IFNULL(SUM(SC.SC_CNT), 0) AS sc_cnt , IFNULL(SUM(LM.DELAY_CNT), 0) AS delay_cnt , IFNULL(SUM(DLY.DELAY_CNT), 0) AS delay_cnt FROM TB_LOAN_MST LM LEFT OUTER JOIN ( SELECT LOAN_NO , COUNT(1) AS SC_CNT , SUM(CASE WHEN RECV_YN = 'Y' THEN 1 ELSE NULL END) AS RECV_CNT , SUM(CASE WHEN RECV_YN = 'Y' THEN NULL ELSE 1 END) AS UNRECV_CNT , SUM(RECV_PRINC_AMT) AS RECV_PRINC_AMT , SUM(RECV_INT_AMT) AS RECV_INT_AMT , SUM(RECV_TOT_AMT) AS RECV_TOT_AMT , SUM(REMAIN_PRINC_AMT) AS REMAIN_PRINC_AMT , SUM(REMAIN_INT_AMT) AS REMAIN_INT_AMT , SUM(REMAIN_TOT_AMT) AS REMAIN_TOT_AMT , SUM(CASE WHEN SC_DATE >= DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_PRINC_AMT ELSE NULL END) AS PRE_PRINC_AMT , SUM(CASE WHEN SC_DATE >= DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_INT_AMT ELSE NULL END) AS PRE_INC_AMT , SUM(CASE WHEN SC_DATE < DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_PRINC_AMT ELSE NULL END) AS POST_PRINC_AMT , SUM(CASE WHEN SC_DATE < DATE_FORMAT(NOW(), '%Y-%m-%d') THEN REMAIN_INT_AMT ELSE NULL END) AS POST_INT_AMT FROM TB_LOAN_REPAY_SCHEDULE WHERE USE_YN = 'Y' GROUP BY LOAN_NO ) SC ON SC.LOAN_NO = LM.LOAN_NO LEFT OUTER JOIN ( SELECT LOAN_NO , COUNT(DISTINCT SC_SEQ ) AS DELAY_CNT , SUM(REMAIN_INT_AMT) AS DELAY_INT_AMT FROM TB_LOAN_REPAY_DELAY WHERE USE_YN = 'Y' AND RECV_YN = 'N' GROUP BY LOAN_NO ) DLY ON DLY.LOAN_NO = LM.LOAN_NO JOIN TB_CHAIN TC ON TC.CHAIN_NO = LM.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('R', 'O') JOIN TB_CORP CO ON CO.CORP_CD = LM.CORP_CD JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID WHERE LM.USE_YN = 'Y' AND LM.CHAIN_NO = #{sch_chain_no}
AND LM.CONT_DT >= #{sch_cont_sdt}
AND LM.CONT_DT <= #{sch_cont_edt}
AND LM.LOAN_STATUS = #{sch_loan_status}
AND LM.LOAN_TYPE = #{sch_loan_type}

mapper.insert:
INSERT INTO TB_LOAN_MST (
    LOAN_NO             , CORP_CD           , CHAIN_NO      
    , LOAN_STATUS       , LOAN_TYPE         , SUB_CODE          , CONT_DT
    , PRINC_AMT         , INT_RATE          , LOAN_SDT          , LOAN_DAY          , LOAN_EDT          
    , INT_AMT           , TOT_LOAN_AMT      , DELAY_RATE        
    , USE_YN            , ENT_DTTM      	, ENT_USER_ID
) VALUES (
    #{loan_no}          , #{corp_cd}        , #{chain_no}
    , #{loan_status}    , #{loan_type}      , #{loan_type}       , #{cont_dt}
    , #{princ_amt}      , #{int_rate}       , #{loan_sdt}       , #{loan_day}       , #{loan_edt}       
    , #{int_amt}        , #{tot_loan_amt}   , #{delay_rate}     
    , 'Y'               , NOW()         	, #{ent_user_id}
)

mapper.update:
UPDATE  TB_LOAN_MST
   SET  LOAN_STATUS = #{loan_status} 
        , MEMO      = #{memo}
        , UPT_DTTM = NOW()
        , UPT_USER_ID = #{upt_user_id}
 WHERE  LOAN_NO = #{loan_no}

UPDATE  TB_LOAN_MST
   SET  USE_YN = 'N'
        , UPT_DTTM = NOW()
        , UPT_USER_ID = #{upt_user_id}
 WHERE  LOAN_NO = #{loan_no}

mapper.insert:
INSERT INTO TB_LOAN_REPAY_SCHEDULE (
    LOAN_NO             , SC_SEQ            , SC_DATE       
    , REPAY_PRINC_AMT   , REPAY_INT_AMT     , REPAY_TOT_AMT     , BALANCE_AMT       
    , RECV_PRINC_AMT    , RECV_INT_AMT      , RECV_TOT_AMT      
    , REMAIN_PRINC_AMT  , REMAIN_INT_AMT    , REMAIN_TOT_AMT            
    , ENT_DTTM      	, ENT_USER_ID
) VALUES (
    #{loan_no}          , #{sc_seq}         , #{sc_date}
    , #{repay_princ_amt}, #{repay_int_amt}  , #{repay_tot_amt} , #{balance_amt}
    , #{recv_princ_amt} , #{recv_int_amt}   , #{recv_tot_amt}
    , #{remain_princ_amt}, #{remain_int_amt}, #{remain_tot_amt}
    , NOW()         	, #{ent_user_id}
)

mapper.update:
		{ 
				CALL PRC_LOAN_PREPAY(
						#{prepay_loan_no        , mode=IN, jdbcType=VARCHAR} 
						, #{prepay_recv_date   , mode=IN, jdbcType=VARCHAR}
                        , #{prepay_memo        , mode=IN, jdbcType=VARCHAR}
						, #{prepay_user_id     , mode=IN, jdbcType=VARCHAR}
						, #{resultCode  , mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
						, #{resultMsg   , mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
				) 
		}
