mapper.select:
SELECT SQL_CALC_FOUND_ROWS tb.board_seq , tb.board_tp , GET_CODE_NM('BOARD_TP', BOARD_TP) as brd_tp_nm , tb.view_corp , tb.title , tb.conts , tb.use_yn , tb.popup_yn , IFNULL(tb.view_cnt, 0) as view_cnt , DATE_FORMAT(tb.ent_dttm, '%Y-%m-%d') as ent_dttm , tb.ent_user_id as ent_user_id , DATE_FORMAT(tb.upt_dttm, '%Y-%m-%d') as upt_dttm , tb.upt_user_id as upt_user_id FROM tb_board tb
WHERE 1 = 1
AND tb.board_tp = #{srch_brd_tp}
AND tb.title LIKE CONCAT('%',#{srch_tp_inp},'%')
AND tb.conts LIKE CONCAT('%',#{srch_tp_inp},'%')
ORDER BY ${sidx} ${sord}
ORDER BY tb.ent_dttm DESC
LIMIT ${start}, ${end}

mapper.insert:
/* BoardMapper.insertBoardFileInfo */
	INSERT INTO TB_BOARD_FILE
	(
		BOARD_SEQ
		, FILE_SEQ
		, FILE_NM
		, ALL_FILE_NM
		, FILE_PATH
	)
	SELECT
		${board_seq}
		, ${file_seq}
		, #{file_name}
		, #{origin_file_name}
		, #{file_path}
	FROM DUAL
	ON DUPLICATE KEY UPDATE 
		FILE_NM = #{file_name}
		, ALL_FILE_NM = #{origin_file_name}
		, FILE_PATH = #{file_path}

INSERT INTO tb_board
(
	BOARD_SEQ		, board_tp

	, view_corp

	, title
	, conts
	, use_yn
	, ent_dttm
	, ent_user_id
)
VALUES( 
	#{board_seq}	, #{board_tp}

	, #{view_corp}

	, #{title}
	, #{conts}
	, #{use_yn}
	, NOW()
	, #{user_id}
)

mapper.update:
UPDATE tb_board
SET
	board_tp = #{board_tp}

	, view_corp = #{view_corp}

	, title = #{title}
	, conts = #{conts}
	, use_yn = #{use_yn}
	, upt_dttm = NOW()
	, upt_user_id = #{user_id}
WHERE
	board_seq = ${board_seq}
