mapper.select:
/*CodeMapper.getGroupCodeRetrieve*/ SELECT SQL_CALC_FOUND_ROWS CODE_GRP_CD AS code_grp_cd , CODE_GRP_NM AS code_grp_nm , TRIM(use_yn) AS use_yn , CASE use_yn WHEN 'Y' THEN '사용' WHEN 'N' THEN '미사용' ELSE NULL END AS use_yn_nm FROM TB_CODE WHERE CODE = 'CODE'
AND code_grp_cd LIKE CONCAT('%',#{srch_gr_val},'%')
AND code_grp_nm LIKE CONCAT('%',#{srch_gr_val},'%')
AND use_yn = #{srch_gr_useyn_tp}
ORDER BY ${sidx} ${sord}
ORDER BY ord_no DESC
LIMIT ${start}, ${end}

/*CodeMapper.getLowerCodeRetrieve*/ SELECT SQL_CALC_FOUND_ROWS TC.CODE AS "code" , TC.CODE_NM AS "code_nm" , GC.GRP_CD AS "code_grp_cd" , GC.GRP_NM AS "code_grp_nm" , IFNULL(TC.ORD_NO, 1) AS "ord_no" , TRIM(TC.VIEW_YN) AS "view_yn" , CASE TC.VIEW_YN WHEN 'Y' THEN '노출' WHEN 'N' THEN '비노출' ELSE NULL END AS "view_yn_nm" , TRIM(TC.USE_YN) AS "use_yn" , CASE TC.USE_YN WHEN 'Y' THEN '사용' WHEN 'N' THEN '미사용' ELSE NULL END AS "use_yn_nm" , DATE_FORMAT(TC.ENT_DTTM, '%Y-%m-%m %H:%i:%s') AS "ent_dttm" , DATE_FORMAT(TC.UPT_DTTM, '%Y-%m-%m %H:%i:%s') AS "upt_dttm" , IFNULL(TC.UPT_USER_ID, ENT_USER_ID) AS "upt_user_id" , TC.RMKS AS "rmks" , TC.CHK_CD1 AS "chk_cd1" , TC.CHK_CD2 AS "chk_cd2" FROM TB_CODE TC JOIN ( SELECT CODE_GRP_CD GRP_CD , CODE_NM GRP_NM FROM TB_CODE WHERE CODE = 'CODE' ) GC ON GC.GRP_CD = TC.CODE_GRP_CD WHERE 1 =1
AND code_grp_cd = #{srch_clas_code}
AND code_grp_cd = 'X'
AND code != 'CODE'
AND code LIKE CONCAT('%',#{srch_low_val},'%')
AND code_nm LIKE CONCAT('%',#{srch_low_val},'%')
AND use_yn = #{srch_low_useyn_tp}
ORDER BY ${sidx} ${sord}
ORDER BY ord_no ASC

mapper.update:
UPDATE tb_code
   SET code_grp_nm  = #{clas_nm}
	 , use_yn		= #{clas_use_yn}
	 , upt_dttm 	= NOW()
	 , upt_user_id  = #{user_id}
WHERE  code_grp_cd 	= #{clas_code}

mapper.insert:
INSERT
  INTO tb_code (  code_grp_cd
				, code
				, code_grp_nm
				, code_nm
				, ord_no
				, view_yn
				, use_yn
				, rmks
				, chk_cd1
				, chk_cd2
				, ent_dttm
				, upt_user_id)
VALUES (  #{low_gr_code}
		, #{low_code}
		, #{low_gr_nm}
		, #{low_code_nm}
		, #{low_sq}
		, #{low_view_yn}
		, #{low_use_yn}
		, #{low_rmks}
		, #{chk_cd1}
		, #{chk_cd2}
		, NOW()
		, #{user_id})

mapper.update:
UPDATE tb_code
   SET code_nm  = #{low_code_nm}
	 , ord_no 	= #{low_sq}
	 , view_yn 	= #{low_view_yn}
	 , use_yn 	= #{low_use_yn}
	 , rmks 	= #{low_rmks}
	 , chk_cd1  = #{chk_cd1}
	 , chk_cd2  = #{chk_cd2}
	 , upt_dttm  = NOW()
	 , upt_user_id  = #{user_id}
 WHERE code_grp_cd 	= #{low_gr_code}
   AND code = #{low_code}

mapper.delete:
DELETE FROM tb_code
 WHERE code_grp_cd 	= #{low_gr_code}
   AND code = #{low_code}
