mapper.insert:
 INSERT INTO tb_menu (
	  menu_cd			, site_gb_cd			, menu_nm			, menu_prnt_cd		, menu_tp
	, menu_url			, menu_ord				, upt_dttm			, ent_dttm			, use_yn
	, view_yn			, upt_user_id

	, view_icon


	) VALUES (
	  #{menu_cd}		, #{site_gb_cd}			, #{menu_nm}		, #{menu_prnt_cd}	, #{menu_tp}
	, #{menu_url}		, #{menu_ord}			, NOW()				, NOW()				, #{menu_use_yn}
	, 'Y'				, #{upt_user_id}

	, #{view_icon}

	)

mapper.update:
UPDATE tb_menu
SET menu_nm = #{menu_nm}

  , menu_cd = #{menu_cd}

  , menu_tp = #{menu_tp}
  , menu_url = #{menu_url}
  , menu_ord = #{menu_ord}
  , upt_dttm = NOW()
  , use_yn = #{menu_use_yn}
  , view_yn = #{view_yn}
  , upt_user_id = #{upt_user_id}
  , view_icon = #{view_icon}
WHERE menu_cd = #{menu_cd} and site_gb_cd = #{site_gb_cd}

mapper.delete:
DELETE
FROM tb_menu
WHERE
	site_gb_cd=#{site_gb_cd}
AND
	menu_cd=#{menu_cd}
