mapper.select:
/* VanDocuMapper.getMapCodeList */ SELECT SQL_CALC_FOUND_ROWS M.MAP_SEQ AS map_seq , M.MAP_PCODE AS map_pcode , GET_CODE_NM('MAP_PCODE', M.MAP_PCODE) AS map_pcode_nm , M.MAP_CODE AS map_code
, GET_CODE_NM('CARD_ACQ', M.MAP_CODE) AS map_code_nm
, GET_CODE_NM('CARD_ISS', M.MAP_CODE) AS map_code_nm
, GET_CODE_NM('CARD_ACQ', M.MAP_CODE) AS map_code_nm
, ( SELECT CHAIN_NM FROM TB_CHAIN WHERE USE_YN = 'Y' AND CHAIN_NO = M.MAP_CODE LIMIT 1 ) AS map_code_nm
, ( SELECT CORP_NM FROM TB_CORP WHERE CORP_TYPE = 'LN' AND USE_YN = 'Y' AND CORP_CD = M.MAP_CODE LIMIT 1 ) AS map_code_nm
, 'error' AS map_code_nm
, M.MAP_TEXT AS map_text , IFNULL(M.USE_YN, 'N') AS use_yn , IFNULL(M.upt_user_id, M.ent_user_id) AS upt_user_id , DATE_FORMAT(IFNULL(M.upt_dttm, M.ent_dttm), '%Y-%m-%d %H:%i:%s') AS upt_dttm FROM TB_MAP_CODE M WHERE M.MAP_PCODE = #{map_pcode} AND M.MAP_CODE = #{map_code}
ORDER BY ${sidx} ${sord}
ORDER BY M.MAP_PCODE, M.MAP_CODE
LIMIT ${start}, ${end}

mapper.insert:
		INSERT INTO TB_MAP_CODE (
                        MAP_PCODE        , MAP_CODE      , MAP_TEXT      , USE_YN        , ENT_USER_ID, ENT_DTTM
		)
		VALUES(
                        #{map_pcode}    , #{map_code}   , #{map_text}   , #{use_yn}     , #{ent_user_id}, NOW()
		)

mapper.update:
UPDATE  TB_MAP_CODE
   SET  MAP_TEXT          = #{map_text}
        , USE_YN          = #{use_yn}
        , UPT_USER_ID     = #{upt_user_id}
        , UPT_DTTM        = NOW()
 WHERE  MAP_SEQ  = ${map_seq}

mapper.select:
/* VanDocuMapper.getScrapDataSumm */ SELECT SQL_CALC_FOUND_ROWS V.CHAIN_NO as chain_no , TC.CHAIN_NM as chain_nm , V.CONF_DT as conf_dt , IFNULL(SUM(V.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(V.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(V.CNCL_CNT), 0) AS cncl_cnt , IFNULL(SUM(V.CNCL_AMT), 0) AS cncl_amt , IFNULL(SUM(V.TOT_CNT), 0) AS tot_cnt , IFNULL(SUM(V.TOT_AMT), 0) AS tot_amt , IFNULL(SUM(V.UN_SETTLE_CNT), 0) AS error_cnt FROM ( SELECT 'VAN' AS GB , CHAIN_NO , SUBSTR(CONF_DTTM, 1, 10) AS CONF_DT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN 1 ELSE NULL END) AS CONF_CNT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN ABS(CONF_AMT) ELSE NULL END) AS CONF_AMT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN NULL ELSE 1 END) AS CNCL_CNT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN NULL ELSE ABS(CONF_AMT) END * -1) AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM((ABS(CONF_AMT) * CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN 1 ELSE -1 END)) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS UN_SETTLE_CNT FROM TB_SCRAP_VAN_DATA T1 WHERE CONF_DTTM >= #{sch_conf_sdt} AND CONF_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59') AND PROC_FG = 'Y'
AND T1.CONF_NO = #{schgb_conf_val}
AND T1.CARD_NO = #{schgb_conf_val}
AND T1.CONF_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(CONF_DTTM, 1, 10) UNION ALL SELECT 'DELI' AS GB , CHAIN_NO , SUBSTR(ORDER_DTTM, 1, 10) AS CONF_DT , COUNT(1) AS CONF_CNT , SUM(ORDER_AMT) AS CONF_AMT , 0 AS CNCL_CNT , 0 AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM(ORDER_AMT) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS SETTLE_CNT FROM TB_SCRAP_DELI_DATA T1 WHERE ORDER_DTTM >= #{sch_conf_sdt} AND ORDER_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59') AND PROC_FG = 'Y'
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(ORDER_DTTM, 1, 10) ) V JOIN TB_CHAIN TC ON TC.CHAIN_NO = V.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('O', 'R') WHERE 1 = 1
AND TC.AGENCY_CD = #{sch_agency_cd}
AND TC.BIZ_NO = #{schgb_chain_val}
AND TC.CHAIN_NM LIKE CONCAT('%', #{schgb_chain_val}, '%')
AND V.CHAIN_NO = #{sch_chain_no}
GROUP BY V.CHAIN_NO, TC.CHAIN_NM, V.CONF_DT
ORDER BY ${sidx} ${sord}
ORDER BY V.CHAIN_NO, V.CONF_DT DESC
LIMIT ${start}, ${end}

/* VanDocuMapper.getScrapDataSummTotal */ SELECT IFNULL(SUM(V.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(V.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(V.CNCL_CNT), 0) AS cncl_cnt , IFNULL(SUM(V.CNCL_AMT), 0) AS cncl_amt , IFNULL(SUM(V.TOT_CNT), 0) AS tot_cnt , IFNULL(SUM(V.TOT_AMT), 0) AS tot_amt , IFNULL(SUM(V.UN_SETTLE_CNT), 0) AS error_cnt FROM ( SELECT 'VAN' AS GB , CHAIN_NO , SUBSTR(CONF_DTTM, 1, 10) AS CONF_DT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN 1 ELSE NULL END) AS CONF_CNT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN ABS(CONF_AMT) ELSE NULL END) AS CONF_AMT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN NULL ELSE 1 END) AS CNCL_CNT , SUM(CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN NULL ELSE ABS(CONF_AMT) END * -1) AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM((ABS(CONF_AMT) * CASE WHEN CONF_GB IN ('승인', '승인(취소)') THEN 1 ELSE -1 END)) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS UN_SETTLE_CNT FROM TB_SCRAP_VAN_DATA T1 WHERE CONF_DTTM >= #{sch_conf_sdt} AND CONF_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59') AND PROC_FG = 'Y'
AND T1.CONF_NO = #{schgb_conf_val}
AND T1.CARD_NO = #{schgb_conf_val}
AND T1.CONF_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(CONF_DTTM, 1, 10) UNION ALL SELECT 'DELI' AS GB , CHAIN_NO , SUBSTR(ORDER_DTTM, 1, 10) AS CONF_DT , COUNT(1) AS CONF_CNT , SUM(ORDER_AMT) AS CONF_AMT , 0 AS CNCL_CNT , 0 AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM(ORDER_AMT) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS SETTLE_CNT FROM TB_SCRAP_DELI_DATA T1 WHERE ORDER_DTTM >= #{sch_conf_sdt} AND ORDER_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59') AND PROC_FG = 'Y'
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(ORDER_DTTM, 1, 10) ) V JOIN TB_CHAIN TC ON TC.CHAIN_NO = V.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('O', 'R')
AND TC.BIZ_NO = #{schgb_chain_val}
AND TC.CHAIN_NM LIKE CONCAT('%', #{schgb_chain_val}, '%')
AND V.CHAIN_NO = #{sch_chain_no}
AND TC.AGENCY_CD = #{sch_agency_cd}
WHERE 1 = 1

/* VanDocuMapper.getVanDocuSumm */
SELECT  SQL_CALC_FOUND_ROWS 
        VD.CHAIN_NO             AS chain_no
        , TC.CHAIN_NM           AS chain_nm
        , T2.BIZ_NO             AS biz_no
        , VD.CONF_DT            AS conf_dt
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'A' THEN 1 ELSE NULL END), 0)                       AS conf_cnt
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'A' THEN ABS(VD.CONF_AMT) ELSE NULL END), 0)        AS conf_amt
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'C' THEN 1 ELSE NULL END), 0)                       AS cncl_cnt
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'C' THEN ABS(VD.CONF_AMT) ELSE NULL END) * -1  , 0) AS cncl_amt
    FROM  TB_VAN_DOCU VD
        JOIN TB_CHAIN  TC ON TC.CHAIN_NO = VD.CHAIN_NO




                AND TC.BIZ_NO  = #{schgb_chain_val}


                AND TC.CHAIN_NM LIKE CONCAT('%', #{schgb_chain_val}, '%')




        AND V.CHAIN_NO = #{sch_chain_no}


           AND  TC.AGENCY_CD = #{sch_agency_cd}


   WHERE  1 = 1      
        AND  VD.CONF_DT   >=  #{sch_conf_sdt} 
        AND  VD.CONF_DT   <=  #{sch_conf_edt}         




                        AND VD.CONF_NO  = #{schgb_conf_val}


                        AND VD.CARD_NO  = #{schgb_conf_val}


                        AND VD.CONF_AMT  = #{schgb_conf_val}



 GROUP 	BY VD.CHAIN_NO, TC.CHAIN_NM, TC.BIZ_NO, VD.CONF_DT


                        ORDER BY ${sidx} ${sord}


                        ORDER BY 1 DESC



 LIMIT  ${start}, ${end}

SELECT  SQL_CALC_FOUND_ROWS 
        VD.CARD_ACQ             					AS card_acq
        , GET_CODE_NM('CARD_ACQ', VD.CARD_ACQ)                          AS card_acq_nm 
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'A' THEN 1 ELSE NULL END), 0)                       AS conf_cnt
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'A' THEN ABS(VD.CONF_AMT) ELSE NULL END), 0)        AS conf_amt
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'C' THEN 1 ELSE NULL END), 0)                       AS cncl_cnt
        , IFNULL(SUM(CASE WHEN VD.CONF_GB = 'C' THEN ABS(VD.CONF_AMT) ELSE NULL END) * -1  , 0) AS cncl_amt
        , IFNULL(SUM((ABS(VD.CONF_AMT) * CASE WHEN VD.CONF_GB = 'A' THEN 1 ELSE -1 END)), 0)    AS tot_amt 
  FROM  TB_VAN_DOCU VD
 WHERE	VD.CHAIN_NO  = #{sch_sub_chain_no} 
   AND  VD.CONF_DT   = #{sch_sub_conf_dt} 




                        AND VD.CONF_NO  = #{schgb_conf_val}


                        AND VD.CARD_NO  = #{schgb_conf_val}


                        AND VD.CONF_AMT  = #{schgb_conf_val}



 GROUP 	BY VD.CARD_ACQ,    GET_CODE_NM('CARD_ACQ', VD.CARD_ACQ) 


                ORDER BY ${sidx} ${sord}


                ORDER BY 1 DESC

SELECT SQL_CALC_FOUND_ROWS VD.DOCU_SEQ AS docu_seq , VD.CHAIN_NO AS chain_no , VD.VAN_CD AS van_cd , GET_CODE_NM('VAN_CD', VD.VAN_CD) AS van_cd_nm , VD.CARD_REG_NO AS card_reg_no , VD.TRANS_ID AS trans_id , VD.CARD_ISS AS card_iss , GET_CODE_NM('CARD_ISS', VD.CARD_ISS) AS card_iss_nm , VD.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', VD.CARD_ACQ) AS card_acq_nm , VD.CARD_NO AS card_no , VD.INSTALL_MON AS install_mon , VD.CONF_NO AS conf_no , VD.CONF_GB AS conf_gb , GET_CODE_NM('CONF_GB', VD.CONF_GB) AS conf_gb_nm , VD.CONF_DT AS conf_dt , VD.CONF_TM AS conf_tm , CONCAT(VD.CONF_DT, ' ', VD.CONF_TM) AS conf_dttm , VD.CONF_AMT * (CASE WHEN VD.CONF_GB = 'C' THEN -1 ELSE 1 END)AS conf_amt , VD.CARD_TYPE AS card_type , GET_CODE_NM('CARD_TYPE', VD.CARD_TYPE) AS card_type_nm , IFNULL(VD.ORG_CONF_DT, '') AS org_conf_dt , VD.CNCL AS cncl_yn , VD.CNCL_DOCU_SEQ AS cncl_docu_seq , VD.CNCL_USER_ID AS cncl_user_id , VD.CNCL_DATE AS cncl_date , VD.CNCL_TIME AS cncl_time , CASE WHEN VD.PROC_FG = 'Y' THEN '사전정산' ELSE '대기중' END AS proc_fg_nm , IFNULL(DATE_FORMAT(VD.PROC_DATE, '%Y-%m-%d %h:%i:%s'), '') AS proc_date , VD.PROC_RESULT AS proc_result , VD.SC_SEQ AS sc_seq , IFNULL(GET_CODE_NM('WD_STATUS', W.WD_STATUS), '미정산') AS wd_status_nm , VD.ENT_USER_ID AS ent_user_id , IFNULL(DATE_FORMAT(VD.ENT_DTTM, '%Y-%m-%d %h:%i:%s'), '') AS ent_dttm FROM TB_VAN_DOCU VD LEFT OUTER JOIN TB_WITHDRAW W ON W.DOCU_SEQ = VD.DOCU_SEQ WHERE VD.CHAIN_NO = #{sch_sub_chain_no} AND VD.CONF_DT = #{sch_sub_conf_dt}
AND FIND_IN_SET(VD.CARD_ACQ, #{schlist_card_acq})
AND VD.CONF_NO = #{schgb_conf_val}
AND VD.CARD_NO = #{schgb_conf_val}
AND VD.CONF_AMT = #{schgb_conf_val}
ORDER BY ${sidx} ${sord}
ORDER BY VD.DOCU_SEQ DESC
LIMIT ${start}, ${end}

SELECT SQL_CALC_FOUND_ROWS VT.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , VT.PROC_RESULT AS proc_result , IFNULL(SUM(VT.UN_PROC_CNT), 0) AS un_proc_cnt FROM ( SELECT 'VAN' AS GB , CHAIN_NO AS CHAIN_NO , IFNULL(PROC_RESULT, '미처리') AS PROC_RESULT , COUNT(1) AS UN_PROC_CNT FROM TB_SCRAP_VAN_DATA T1 WHERE CONF_DTTM >= #{sch_unproc_conf_dt} AND CONF_DTTM <= CONCAT(#{sch_unproc_conf_dt}, ' 23:59:59') AND CHAIN_NO = #{sch_unproc_chain_no} AND USE_YN = 'Y' AND PROC_FG = 'N' GROUP BY CHAIN_NO, IFNULL(PROC_RESULT, '미처리') UNION ALL SELECT 'DELI' AS GB , CHAIN_NO , IFNULL(PROC_RESULT, '미처리') AS proc_result , COUNT(1) AS unproc_cnt FROM TB_SCRAP_DELI_DATA WHERE ORDER_DTTM >= #{sch_unproc_conf_dt} AND ORDER_DTTM <= CONCAT(#{sch_unproc_conf_dt}, ' 23:59:59') AND CHAIN_NO = #{sch_unproc_chain_no} AND USE_YN = 'Y' AND PROC_FG = 'N' GROUP BY CHAIN_NO, IFNULL(PROC_RESULT, '미처리') ) VT JOIN TB_CHAIN TC ON TC.CHAIN_NO = VT.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('O', 'R') GROUP BY VT.CHAIN_NO, TC.CHAIN_NM, VT.PROC_RESULT
ORDER BY ${sidx} ${sord}
ORDER BY 1 DESC
LIMIT ${start}, ${end}

mapper.update:
{ 
        CALL PRC_SCRAP_TRANS_VAN_DOCU(
                #{chainNo, mode=IN, jdbcType=VARCHAR}
                , #{userId, mode=IN, jdbcType=VARCHAR}
                , #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
                , #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
        ) 
}

{ 
        CALL PRC_SCRAP_TRANS_DELI_DOCU(
                #{chainNo, mode=IN, jdbcType=VARCHAR}
                , #{userId, mode=IN, jdbcType=VARCHAR}
                , #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
                , #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
        ) 
}
