mapper.insert:
		INSERT INTO TB_MAP_CODE (
                        MAP_PCODE        , MAP_CODE      , MAP_TEXT      , USE_YN        , ENT_USER_ID, ENT_DTTM
		)
		VALUES(
                        #{map_pcode}    , #{map_code}   , #{map_text}   , #{use_yn}     , #{ent_user_id}, NOW()
		)

mapper.update:
UPDATE  TB_MAP_CODE
   SET  MAP_TEXT          = #{map_text}
        , USE_YN          = #{use_yn}
        , UPT_USER_ID     = #{upt_user_id}
        , UPT_DTTM        = NOW()
 WHERE  MAP_SEQ  = ${map_seq}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS V.CHAIN_NO as chain_no , TC.CHAIN_NM as chain_nm , V.CONF_DT as conf_dt , IFNULL(SUM(V.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(V.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(V.CNCL_CNT), 0) AS cncl_cnt , IFNULL(SUM(V.CNCL_AMT), 0) AS cncl_amt , IFNULL(SUM(V.TOT_CNT), 0) AS tot_cnt , IFNULL(SUM(V.TOT_AMT), 0) AS tot_amt , IFNULL(SUM(V.UN_SETTLE_CNT), 0) AS error_cnt FROM ( SELECT 'VAN' AS GB , CHAIN_NO , SUBSTR(CONF_DTTM, 1, 10) AS CONF_DT , SUM(CASE WHEN CONF_GB = '승인' THEN 1 ELSE NULL END) AS CONF_CNT , SUM(CASE WHEN CONF_GB = '승인' THEN CONF_AMT ELSE NULL END) AS CONF_AMT , SUM(CASE WHEN CONF_GB = '승인' THEN NULL ELSE 1 END) AS CNCL_CNT , SUM(CASE WHEN CONF_GB = '승인' THEN NULL ELSE CONF_AMT END) AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM((CONF_AMT * CASE WHEN CONF_GB = '승인' THEN 1 ELSE -1 END)) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS UN_SETTLE_CNT FROM TB_SCRAP_VAN_DATA T1 WHERE CONF_DTTM >= #{sch_conf_sdt} AND CONF_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59')
AND T1.CONF_NO = #{schgb_conf_val}
AND T1.CARD_NO = #{schgb_conf_val}
AND T1.CONF_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(CONF_DTTM, 1, 10) UNION ALL SELECT 'DELI' AS GB , CHAIN_NO , SUBSTR(ORDER_DTTM, 1, 10) AS CONF_DT , COUNT(1) AS CONF_CNT , SUM(ORDER_AMT) AS CONF_AMT , 0 AS CNCL_CNT , 0 AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM(ORDER_AMT) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS SETTLE_CNT FROM TB_SCRAP_DELI_DATA WHERE ORDER_DTTM >= #{sch_conf_sdt} AND ORDER_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59')
AND CARD_ACQ = #{sch_card_acq}
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(ORDER_DTTM, 1, 10) ) V JOIN TB_CHAIN TC ON TC.CHAIN_NO = V.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('O', 'R') WHERE 1 = 1
AND TC.AGENCY_NO = #{sch_agency_no}
AND TC.BIZ_NO = #{schgb_chain_val}
AND TC.CHAIN_NM LIKE CONCAT('%', #{schgb_chain_val}, '%')
GROUP BY V.CHAIN_NO, TC.CHAIN_NM, V.CONF_DT
ORDER BY ${sidx} ${sord}
ORDER BY V.CHAIN_NO, V.CONF_DT DESC
LIMIT ${start}, ${end}

SELECT IFNULL(SUM(V.CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(V.CONF_AMT), 0) AS conf_amt , IFNULL(SUM(V.CNCL_CNT), 0) AS cncl_cnt , IFNULL(SUM(V.CNCL_AMT), 0) AS cncl_amt , IFNULL(SUM(V.TOT_CNT), 0) AS tot_cnt , IFNULL(SUM(V.TOT_AMT), 0) AS tot_amt , IFNULL(SUM(V.UN_SETTLE_CNT), 0) AS error_cnt FROM ( SELECT 'VAN' AS GB , CHAIN_NO , SUBSTR(CONF_DTTM, 1, 10) AS CONF_DT , SUM(CASE WHEN CONF_GB = '승인' THEN 1 ELSE NULL END) AS CONF_CNT , SUM(CASE WHEN CONF_GB = '승인' THEN CONF_AMT ELSE NULL END) AS CONF_AMT , SUM(CASE WHEN CONF_GB = '승인' THEN NULL ELSE 1 END) AS CNCL_CNT , SUM(CASE WHEN CONF_GB = '승인' THEN NULL ELSE CONF_AMT END) AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM((CONF_AMT * CASE WHEN CONF_GB = '승인' THEN 1 ELSE -1 END)) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS UN_SETTLE_CNT FROM TB_SCRAP_VAN_DATA T1 WHERE CONF_DTTM >= #{sch_conf_sdt} AND CONF_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59')
AND T1.CONF_NO = #{schgb_conf_val}
AND T1.CARD_NO = #{schgb_conf_val}
AND T1.CONF_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(CONF_DTTM, 1, 10) UNION ALL SELECT 'DELI' AS GB , CHAIN_NO , SUBSTR(ORDER_DTTM, 1, 10) AS CONF_DT , COUNT(1) AS CONF_CNT , SUM(ORDER_AMT) AS CONF_AMT , 0 AS CNCL_CNT , 0 AS CNCL_AMT , COUNT(1) AS TOT_CNT , SUM(ORDER_AMT) AS TOT_AMT , SUM((CASE WHEN PROC_FG = 'N' THEN 1 ELSE NULL END)) AS SETTLE_CNT FROM TB_SCRAP_DELI_DATA WHERE ORDER_DTTM >= #{sch_conf_sdt} AND ORDER_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59')
AND CARD_ACQ = #{sch_card_acq}
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_NO = #{schgb_conf_val}
AND T1.ORDER_AMT = #{schgb_conf_val}
GROUP BY CHAIN_NO, SUBSTR(ORDER_DTTM, 1, 10) ) V JOIN TB_CHAIN TC ON TC.CHAIN_NO = V.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('O', 'R') WHERE 1 = 1
AND TC.AGENCY_NO = #{sch_agency_no}
AND TC.BIZ_NO = #{schgb_chain_val}
AND TC.CHAIN_NM LIKE CONCAT('%', #{schgb_chain_val}, '%')

       SELECT  SQL_CALC_FOUND_ROWS 
               T1.CHAIN_NO             AS chain_no
               , T2.CHAIN_NM           AS chain_nm
               , T2.BIZ_NO             AS biz_no
               , T1.CONF_DT            AS conf_dt
               , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'A' THEN 1 ELSE NULL END), 0)               AS conf_cnt
               , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'A' THEN T1.CONF_AMT ELSE NULL END), 0)     AS conf_amt
               , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'C' THEN 1 ELSE NULL END), 0)               AS cncl_cnt
               , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'C' THEN T1.CONF_AMT * -1 ELSE NULL END), 0) AS cncl_amt
         FROM  TB_VAN_DOCU T1
               JOIN TB_CHAIN  T2 ON T2.CHAIN_NO = T1.CHAIN_NO




                       AND T2.BIZ_NO  = #{schgb_chain_val}


                       AND T2.CHAIN_NM LIKE CONCAT('%', #{schgb_chain_val}, '%')



  WHERE  1 = 1      
       AND  CONF_DT   >=  #{sch_conf_sdt} 
       AND  CONF_DT   <=  #{sch_conf_edt}         




                       AND T1.CONF_NO  = #{schgb_conf_val}


                       AND T1.CARD_NO  = #{schgb_conf_val}


                       AND T1.CONF_AMT  = #{schgb_conf_val}



GROUP 	BY T1.CHAIN_NO, T2.CHAIN_NM, T2.BIZ_NO, T1.CONF_DT


                       ORDER BY ${sidx} ${sord}


                       ORDER BY agency_no DESC



LIMIT  ${start}, ${end}

SELECT  SQL_CALC_FOUND_ROWS 
        T1.CARD_ACQ             					AS card_acq
        , GET_CODE_NM('CARD_ACQ', T1.CARD_ACQ)                          AS card_acq_nm 
        , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'A' THEN 1 ELSE NULL END), 0)               AS conf_cnt
        , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'A' THEN T1.CONF_AMT ELSE NULL END), 0)     AS conf_amt
        , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'C' THEN 1 ELSE NULL END), 0)               AS cncl_cnt
        , IFNULL(SUM(CASE WHEN T1.CONF_GB = 'C' THEN T1.CONF_AMT * -1 ELSE NULL END), 0) AS cncl_amt
        , SUM((CONF_AMT * CASE WHEN CONF_GB = 'A' THEN 1 ELSE -1 END))		        AS tot_amt 
  FROM  TB_VAN_DOCU T1
 WHERE	CHAIN_NO  = #{sch_sub_chain_no} 
   AND  CONF_DT   = #{sch_sub_conf_dt} 



                        AND T1.CONF_NO  = #{schgb_conf_val}


                        AND T1.CARD_NO  = #{schgb_conf_val}


                        AND T1.CONF_AMT  = #{schgb_conf_val}



 GROUP 	BY T1.CARD_ACQ,    GET_CODE_NM('CARD_ACQ', T1.CARD_ACQ) 


                ORDER BY ${sidx} ${sord}


                ORDER BY T1.CARD_ACQ DESC


LIMIT  ${start}, ${end}

SELECT SQL_CALC_FOUND_ROWS T1.DOCU_SEQ AS docu_seq , T1.CHAIN_NO AS chain_no , T1.VAN_CD AS van_cd , GET_CODE_NM('VAN_CD', T1.VAN_CD) AS van_cd_nm , T1.CARD_REG_NO AS card_reg_no , T1.TRANS_ID AS trans_id , T1.CARD_ISS AS card_iss , GET_CODE_NM('CARD_ISS', T1.CARD_ISS) AS card_iss_nm , T1.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', T1.CARD_ACQ) AS card_acq_nm , T1.CARD_NO AS card_no , T1.INSTALL_MON AS install_mon , T1.CONF_NO AS conf_no , T1.CONF_GB AS conf_gb , GET_CODE_NM('CONF_GB', T1.CONF_GB) AS conf_gb_nm , T1.CONF_DT AS conf_dt , T1.CONF_TM AS conf_tm , CONCAT(T1.CONF_DT, ' ', T1.CONF_TM) AS conf_dttm , T1.CONF_AMT AS conf_amt , T1.CARD_TYPE AS card_type , GET_CODE_NM('CARD_TYPE', T1.CARD_TYPE) AS card_type_nm , IFNULL(T1.ORG_CONF_DT, '') AS org_conf_dt , T1.CNCL AS cncl_yn , T1.CNCL_DOCU_SEQ AS cncl_docu_seq , T1.CNCL_USER_ID AS cncl_user_id , T1.CNCL_DATE AS cncl_date , T1.CNCL_TIME AS cncl_time , CASE WHEN T1.PROC_FG = 'Y' THEN '사전정산' ELSE '대기중' END AS proc_fg_nm , IFNULL(DATE_FORMAT(T1.PROC_DATE, '%Y-%m-%d %h:%i:%s'), '') AS proc_date , T1.PROC_RESULT AS proc_result , T1.SC_SEQ AS sc_seq , IFNULL(GET_CODE_NM('WD_STATUS', W.WD_STATUS), '미정산') AS wd_status_nm , T1.ENT_USER_ID AS ent_user_id , IFNULL(DATE_FORMAT(T1.ENT_DTTM, '%Y-%m-%d %h:%i:%s'), '') AS ent_dttm FROM TB_VAN_DOCU T1 LEFT OUTER JOIN TB_WITHDRAW W ON W.DOCU_SEQ = T1.DOCU_SEQ WHERE T1.CHAIN_NO = #{schlist_chain_no} AND T1.CONF_DT = #{schlist_conf_dt}
AND FIND_IN_SET(T1.CARD_ACQ, #{schlist_card_acq})
AND T1.CONF_NO = #{schlist_conf_val}
AND T1.CARD_NO = #{schlist_conf_val}
AND T1.CONF_AMT = #{schlist_conf_val}
ORDER BY ${sidx} ${sord}
ORDER BY T1.DOCU_SEQ DESC
LIMIT ${start}, ${end}

SELECT SQL_CALC_FOUND_ROWS VT.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , VT.PROC_RESULT AS proc_result , IFNULL(SUM(VT.UN_PROC_CNT), 0) AS un_proc_cnt FROM ( SELECT 'VAN' AS GB , CHAIN_NO AS CHAIN_NO , IFNULL(PROC_RESULT, '미처리') AS PROC_RESULT , COUNT(1) AS UN_PROC_CNT FROM TB_SCRAP_VAN_DATA T1 WHERE CONF_DTTM >= #{sch_unproc_conf_dt} AND CONF_DTTM <= CONCAT(#{sch_unproc_conf_dt}, ' 23:59:59') AND CHAIN_NO = #{sch_unproc_chain_no} AND USE_YN = 'Y' AND PROC_FG = 'N' GROUP BY CHAIN_NO, IFNULL(PROC_RESULT, '미처리') UNION ALL SELECT 'DELI' AS GB , CHAIN_NO , IFNULL(PROC_RESULT, '미처리') AS proc_result , COUNT(1) AS unproc_cnt FROM TB_SCRAP_DELI_DATA WHERE ORDER_DTTM >= #{sch_unproc_conf_dt} AND ORDER_DTTM <= CONCAT(#{sch_unproc_conf_dt}, ' 23:59:59') AND CHAIN_NO = #{sch_unproc_chain_no} AND USE_YN = 'Y' AND PROC_FG = 'N' GROUP BY CHAIN_NO, IFNULL(PROC_RESULT, '미처리') ) VT JOIN TB_CHAIN TC ON TC.CHAIN_NO = VT.CHAIN_NO AND TC.USE_YN = 'Y' AND TC.SVC_STAT IN ('O', 'R') GROUP BY VT.CHAIN_NO, TC.CHAIN_NM, VT.PROC_RESULT
ORDER BY ${sidx} ${sord}
ORDER BY 1 DESC
LIMIT ${start}, ${end}

mapper.update:
{ 
        CALL PRC_SCRAP_TRANS_VAN_DOCU(
                #{chainNo, mode=IN, jdbcType=VARCHAR}
                , #{userId, mode=IN, jdbcType=VARCHAR}
                , #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
                , #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
        ) 
}

{ 
        CALL PRC_SCRAP_TRANS_DELI_DOCU(
                #{chainNo, mode=IN, jdbcType=VARCHAR}
                , #{userId, mode=IN, jdbcType=VARCHAR}
                , #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
                , #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
        ) 
}
