mapper.select:
SELECT SQL_CALC_FOUND_ROWS TU.USER_ID AS user_id , TU.USER_NM AS user_nm , TU.CORP_CD AS corp_cd , TC.CORP_NM AS corp_nm , TU.CORP_TYPE AS corp_type , GET_CODE_NM('CORP_TYPE', TU.CORP_TYPE ) AS corp_type_nm , TU.user_pwd AS user_pwd , TU.user_gb AS user_gb , TU.auth_grp_cd AS auth_grp_cd , GET_CODE_NM('AUTH_GRP_CD', TU.auth_grp_cd ) AS auth_grp_nm , IFNULL(TU.hp_no, '-') AS hp_no , TU.tel_no AS tel_no , IFNULL(TU.email, '-') AS email , TU.zip_no , TU.addr , TU.addr_dtl , TU.use_yn , DATE_FORMAT(IFNULL(TU.upt_dttm, TU.ent_dttm), '%Y-%m-%m %H:%i:%s') AS upt_dttm , IFNULL(TU.upt_user_id, TU.ent_user_id) AS upt_user_id FROM TB_USER TU JOIN TB_CORP TC ON TC.CORP_CD = TU.CORP_CD AND TC.USE_YN = 'Y' WHERE 1 = 1
AND TU.USE_YN = 'Y'
AND TU.AUTH_GRP_CD != 'AG0001' /* 시스템 관리자 제외 */
AND TU.USER_ID = #{schgb_user_val}
AND TU.USER_NM LIKE CONCAT('%', #{schgb_user_val}, '%')
AND TU.CORP_TYPE = #{sch_corp_type}
ORDER BY ${sidx} ${sord}
ORDER BY TU.ENT_DTTM DESC

SELECT CORP_CD as code , CORP_NM as codeNm FROM TB_CORP WHERE 1= 1
AND CORP_TYPE = #{corp_type}
AND USE_YN = 'Y'

	SELECT  COUNT(1)
          FROM  TB_USER
         WHERE  USER_ID = #{user_id}

mapper.update:
UPDATE tb_user SET upt_dttm = NOW() , upt_user_id = #{upt_user_id}
, corp_cd = #{corp_cd}
, corp_type = #{corp_type}
, user_pwd = SHA2(#{user_pwd}, 256)
, user_nm = #{user_nm}
, auth_grp_cd = #{auth_grp_cd}
, email = #{email}
, zip_no = #{zip_no}
, addr = #{addr}
, addr_dtl = #{addr_dtl}
, hp_no = #{hp_no}
, tel_no = #{tel_no}
, use_yn = #{use_yn}
WHERE user_id = #{user_id}
