T , IFNULL(SUM(CNCL_PRE_AMT), 0) AS CNCL_PRE_AMT , IFNULL(SUM(RCOUNT), 0) AS RCOUNT , IFNULL(SUM(ERRCOUNT), 0) AS ERRCOUNT , IFNULL(SUM(RESV_AMT), 0) AS RESV_AMT , IFNULL(SUM(UNRESV_AMT), 0) AS UNRESV_AMT , IFNULL(SUM(CARD_FEE_AMT), 0) AS CARD_FEE_AMT , IFNULL(SUM(CARD_RESV_AMT), 0) AS CARD_RESV_AMT , IFNULL(SUM(WD_BASE_AMT), 0) AS WD_BASE_AMT , IFNULL(SUM(SVC_FEE_AMT), 0) AS SVC_FEE_AMT , IFNULL(SUM(CRD_FEE_AMT), 0) AS CRD_FEE_AMT , IFNULL(SUM(EXC_AMT), 0) AS EXC_AMT FROM VW_WITHDRAW_RESV_LIST WL STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = WL.CHAIN_NO AND TC.SVC_STAT = 'O' AND TC.USE_YN = 'Y' AND TC.REMIT_STAT = 'Y' AND TC.CONT_TP = 'REMIT'
