/*WithdrawMapper.getWDCardSummary*/ SELECT SQL_CALC_FOUND_ROWS CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', CARD_ACQ) AS card_acq_nm , IFNULL(SUM(TOT_CNT), 0) AS tot_cnt , IFNULL(SUM(TOT_AMT), 0) AS tot_amt , IFNULL(SUM(CONF_CNT), 0) AS conf_cnt , IFNULL(SUM(CNCL_CNT), 0) AS cncl_cnt , IFNULL(SUM(CONF_AMT), 0) AS conf_amt , IFNULL(SUM(CNCL_AMT), 0) AS cncl_amt , IFNULL(SUM(REMIT_CONF_CNT), 0) AS remit_conf_cnt , IFNULL(SUM(REMIT_CONF_AMT), 0) AS remit_conf_amt , IFNULL(SUM(CNCL_DAY_CNT), 0) AS cncl_day_cnt , IFNULL(SUM(CNCL_DAY_AMT), 0) AS cncl_day_amt , IFNULL(SUM(CNCL_PRE_CNT), 0) AS cncl_pre_cnt , IFNULL(SUM(CNCL_PRE_AMT), 0) AS cncl_pre_amt , IFNULL(SUM(RCOUNT), 0) AS rcount , IFNULL(SUM(ERRCOUNT), 0) AS errcount , IFNULL(SUM(RESV_AMT), 0) AS resv_amt , IFNULL(SUM(UNRESV_AMT), 0) AS unresv_amt , IFNULL(SUM(WD_BASE_AMT), 0) AS wd_base_amt , IFNULL(SUM(SVC_FEE_AMT), 0) AS svc_fee_amt , IFNULL(SUM(EXC_AMT), 0) AS exc_amt FROM VW_WITHDRAW_RESV_LIST WHERE WORK_DATE = #{sch_card_work_date} AND CHAIN_NO = #{sch_card_chain_no} GROUP BY CARD_ACQ, GET_CODE_NM('CARD_ACQ', CARD_ACQ)
