/*WithdrawMapper.getWDResvList*/ SELECT SQL_CALC_FOUND_ROWS WD.WD_NO AS wd_no , WD.WD_STATUS AS wd_status , GET_CODE_NM('WD_STATUS', WD.WD_STATUS) AS wd_status_nm , WD.WORK_DATE AS work_date , WD.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , WD.DOCU_SEQ AS docu_seq , VD.VAN_CD AS van_cd , GET_CODE_NM('VAN_CD', VD.VAN_CD) AS van_cd_nm , WD.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', WD.CARD_ACQ) AS card_acq_nm , VD.CARD_NO AS card_no , WD.CARD_REG_NO AS card_reg_no , VD.CARD_TYPE AS card_type , GET_CODE_NM('CARD_TYPE', VD.CARD_TYPE) AS card_type_nm , WD.CONF_NO AS conf_no , WD.CONF_GB AS conf_gb , CASE WHEN WD.CONF_GB = 'A' THEN '승인' ELSE '취소' END AS conf_gb_nm , WD.CONF_DTTM AS conf_dttm , WD.CONF_AMT AS conf_amt , WD.CARD_BUY_FLAG AS card_buy_flag , WD.CARD_BUY_DATE AS card_buy_date , WD.CARD_FEE_RATE AS fard_fee_rate , WD.CARD_FEE_AMT AS card_fee_amt , IFNULL(WD.CARD_RESV_AMT, 0) AS card_resv_amt , IFNULL(WD.CARD_RESV_DAY, 0) AS card_resv_day , IFNULL(WD.CARD_RESV_DATE, '') AS card_resv_date , IFNULL(WD.SVC_FEE_RATE, 0) AS svc_fee_rate , IFNULL(WD.SVC_FEE_AMT, 0) AS svc_fee_amt , IFNULL(WD.WD_BASE_AMT, 0) AS wd_base_amt , IFNULL(WD.CRD_FEE_RATE, 0) AS crd_fee_rate , IFNULL(WD.CRD_FEE_AMT, 0) AS crd_fee_amt , IFNULL(WD.SUB_FLAG, 'N') AS sub_flag , IFNULL(WD.REMIT_AMT, 0) AS remit_amt , IFNULL(WD.REMIT_NO, '') AS remit_seq , IFNULL(WD.REMIT_DT, '') AS remit_dt , IFNULL(WD.DEPOSIT_FLAG, '') AS deposit_flag , IFNULL(WD.BANK_IN_DATE, '') AS bank_in_date , IFNULL(WD.CARDSALES_FG, '') AS cardsales_fg , WD.USE_YN AS use_yn , IFNULL(WD.WD_MEMO, '') AS wd_memo , WD.CLOSE_YN AS close_yn , DATE_FORMAT(IFNULL(WD.UPT_DTTM, WD.ENT_DTTM), '%Y-%m-%d %H:%i:%s') AS upt_dttm , DATE_FORMAT(IFNULL(WD.ENT_USER_ID, WD.UPT_USER_ID), '%Y-%m-%d %H:%i:%s') AS upt_user_id FROM TB_WITHDRAW WD STRAIGHT_JOIN TB_VAN_DOCU VD ON VD.DOCU_SEQ = WD.DOCU_SEQ STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = WD.CHAIN_NO WHERE 1=1 AND WD.WORK_DATE = #{sch_list_work_date} AND WD.CHAIN_NO = #{sch_list_chain_no} AND WD.WD_STATUS LIKE 'W%'
AND FIND_IN_SET(WD.CARD_ACQ, #{sch_list_card_acq})
AND WD.WD_STATUS IN ('W00000', 'W00C00', 'W00C11', 'W00C12')
AND WD.WD_STATUS NOT IN ('W00000', 'W00C00', 'W00C11', 'W00C12', 'W00906')
AND WD.CONF_DTTM >= #{sch_list_conf_sdt}
AND WD.CONF_DTTM <= CONCAT(#{sch_list_conf_edt} , ' 23:59:59')
AND VD.VAN_CD = #{sch_list_van}
AND WD.CONF_NO = #{sch_list_gb_conf_val}
AND WD.CARD_NO = #{sch_list_gb_conf_val}
AND WD.CONF_AMT = #{sch_list_gb_conf_val}
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

/*WithdrawMapper.getWDResvListTotal*/
SELECT    IFNULL(SUM(WD.CONF_AMT), 0)        AS conf_amt
        , IFNULL(SUM(WD.CARD_FEE_AMT), 0)    AS card_fee_amt
        , IFNULL(SUM(WD.CARD_RESV_AMT), 0)   AS card_resv_amt
        , IFNULL(SUM(WD.SVC_FEE_AMT), 0)     AS svc_fee_amt
        , IFNULL(SUM(WD.WD_BASE_AMT), 0)     AS wd_base_amt
        , IFNULL(SUM(WD.CRD_FEE_AMT), 0)     AS crd_fee_amt
        , IFNULL(SUM(WD.REMIT_AMT), 0)       AS remit_amt                
FROM    TB_WITHDRAW WD
        STRAIGHT_JOIN TB_VAN_DOCU VD ON VD.DOCU_SEQ = WD.DOCU_SEQ
        STRAIGHT_JOIN TB_CHAIN   TC ON TC.CHAIN_NO = WD.CHAIN_NO 
WHERE  1=1
AND     WD.WORK_DATE    = #{sch_list_work_date} 
AND     WD.CHAIN_NO     = #{sch_list_chain_no} 
AND     WD.WD_STATUS    LIKE 'W%'  

    AND  FIND_IN_SET(WD.CARD_ACQ, #{sch_list_card_acq})  



        AND WD.WD_STATUS  IN ('W00000', 'W00C00', 'W00C11', 'W00C12')


        AND WD.WD_STATUS  NOT IN ('W00000', 'W00C00', 'W00C11', 'W00C12', 'W00906')



        AND  WD.CONF_DTTM >= #{sch_list_conf_sdt}


        AND  WD.CONF_DTTM <= CONCAT(#{sch_list_conf_edt} , ' 23:59:59')


        AND  VD.VAN_CD = #{sch_list_van}




            AND WD.CONF_NO  = #{sch_list_gb_conf_val}


            AND WD.CARD_NO  = #{sch_list_gb_conf_val}


            AND WD.CONF_AMT  = #{sch_list_gb_conf_val}

