AND TC.CHAIN_NO = #{sch_chain_no}
STRAIGHT_JOIN TB_USER TU ON TU.USER_ID = TC.CEO_ID STRAIGHT_JOIN TB_CORP CO ON CO.CORP_CD = TC.CORP_CD AND CO.USE_YN = 'Y' WHERE RM.REMIT_DT BETWEEN #{sch_remit_sdt} AND #{sch_remit_edt}

/*WithdrawMapper.getRemitList*/ SELECT SQL_CALC_FOUND_ROWS WD.WD_NO AS wd_no , WD.WD_STATUS AS wd_status , GET_CODE_NM('WD_STATUS', WD.WD_STATUS) AS wd_status_nm , WD.WORK_DATE AS work_date , WD.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , WD.DOCU_SEQ AS docu_seq , VD.VAN_CD AS van_cd , GET_CODE_NM('VAN_CD', VD.VAN_CD) AS van_cd_nm , WD.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', WD.CARD_ACQ) AS card_acq_nm , VD.CARD_NO AS card_no , WD.CARD_REG_NO AS card_reg_no , VD.CARD_TYPE AS card_type , GET_CODE_NM('CARD_TYPE', VD.CARD_TYPE) AS card_type_nm , WD.CONF_NO AS conf_no , WD.CONF_GB AS conf_gb , CASE WHEN WD.CONF_GB = 'A' THEN '승인' ELSE '취소' END AS conf_gb_nm , WD.CONF_DTTM AS conf_dttm , WD.CONF_AMT AS conf_amt , WD.CARD_BUY_FLAG AS card_buy_flag , WD.CARD_BUY_DATE AS card_buy_date , WD.CARD_FEE_RATE AS fard_fee_rate , WD.CARD_FEE_AMT AS card_fee_amt , IFNULL(WD.CARD_RESV_AMT, 0) AS card_resv_amt , IFNULL(WD.CARD_RESV_DAY, 0) AS card_resv_day , IFNULL(WD.CARD_RESV_DATE, '') AS card_resv_date , IFNULL(WD.SVC_FEE_RATE, 0) AS svc_fee_rate , IFNULL(WD.SVC_FEE_AMT, 0) AS svc_fee_amt , IFNULL(WD.WD_BASE_AMT, 0) AS wd_base_amt , IFNULL(WD.CRD_FEE_RATE, 0) AS crd_fee_rate , IFNULL(WD.CRD_FEE_AMT, 0) AS crd_fee_amt , IFNULL(WD.SUB_FLAG, 'N') AS sub_flag , IFNULL(WD.REMIT_AMT, 0) AS remit_amt , IFNULL(WD.REMIT_NO, '') AS remit_seq , IFNULL(WD.REMIT_DT, '') AS remit_dt , IFNULL(WD.DEPOSIT_FLAG, '') AS deposit_flag , IFNULL(WD.BANK_IN_DATE, '') AS bank_in_date , IFNULL(WD.CARDSALES_FG, '') AS cardsales_fg , WD.USE_YN AS use_yn , IFNULL(WD.WD_MEMO, '') AS wd_memo , WD.CLOSE_YN AS close_yn , DATE_FORMAT(IFNULL(WD.UPT_DTTM, WD.ENT_DTTM), '%Y-%m-%d %H:%i:%s') AS upt_dttm , DATE_FORMAT(IFNULL(WD.ENT_USER_ID, WD.UPT_USER_ID), '%Y-%m-%d %H:%i:%s') AS upt_user_id FROM TB_WITHDRAW WD STRAIGHT_JOIN TB_VAN_DOCU VD ON VD.DOCU_SEQ = WD.DOCU_SEQ STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = WD.CHAIN_NO WHERE 1=1 AND WD.REMIT_NO = #{sch_list_remit_no}
AND WD.CONF_NO = #{sch_list_gb_val}
AND WD.CARD_NO = #{sch_list_gb_val}
AND WD.CONF_AMT = #{sch_list_gb_val}
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

/*WithdrawMapper.getRemitSubRecvList*/ SELECT SQL_CALC_FOUND_ROWS SM.SUB_NO AS sub_no , SM.SUB_CODE AS sub_code , GET_CODE_NM ('SUB_CODE', SM.SUB_CODE) AS sub_code_nm , SM.CHAIN_NO AS chain_no , SM.SUB_STATUS AS sub_status , GET_CODE_NM ('SUB_STATUS', SM.SUB_STATUS) AS sub_status_nm , SM.OCCUR_AMT AS occur_amt , SM.OCCUR_BASE_AMT AS occur_base_amt , SM.OCCUR_SVC_AMT AS occur_svc_amt , SM.OCCUR_CRD_AMT AS occur_crd_amt , IFNULL(SR.RECV_AMT, 0) AS recv_amt , IFNULL(SR.RECV_BASE_AMT, 0) AS recv_base_amt , IFNULL(SR.RECV_SVC_AMT, 0) AS recv_svc_amt , IFNULL(SR.RECV_CRD_AMT, 0) AS recv_crd_amt , SM.REMAIN_AMT AS remain_amt , SM.REMAIN_BASE_AMT AS remain_base_amt , SM.REMAIN_SVC_AMT AS remain_svc_amt , SM.REMAIN_CRD_AMT AS remain_crd_amt , IFNULL(SM.SUB_MEMO, '') AS sub_memo FROM TB_SUB_MST SM JOIN ( SELECT SUB_NO, REMIT_NO , SUM(RECV_AMT) AS RECV_AMT , SUM(RECV_BASE_AMT) AS RECV_BASE_AMT , SUM(RECV_SVC_AMT) AS RECV_SVC_AMT , SUM(RECV_CRD_AMT) AS RECV_CRD_AMT FROM TB_SUB_RECEIVE WHERE REMIT_NO = #{sch_sub_recv_remit_no} GROUP BY SUB_NO, REMIT_NO ) SR ON SR.SUB_NO = SM.SUB_NO
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

mapper.update:
{ 
        CALL PRC_REMIT_CANCEL(
                #{remitNo, mode=IN, jdbcType=VARCHAR} 
                , #{userId, mode=IN, jdbcType=VARCHAR}
                , #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
                , #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
        ) 
}

{ 
        CALL PRC_REMIT_SEND(
                #{remitNo, mode=IN, jdbcType=VARCHAR}
                , #{userId, mode=IN, jdbcType=VARCHAR}
                , #{resultCode, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Integer}
                , #{resultMsg, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String}
        ) 
}

mapper.select:
/* WithdrawMapper.getURSummary */ SELECT SQL_CALC_FOUND_ROWS SUBSTR(WD.CONF_DTTM, 1, 10) AS conf_dt , WD.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , TC.BIZ_NO AS biz_no , TC.REMIT_STAT AS remit_stat , WD.WORK_DATE AS work_date , IFNULL(COUNT(1), 0) AS tot_cnt , IFNULL(SUM(WD.CONF_AMT), 0) AS tot_amt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN 1 ELSE NULL END), 0) AS conf_cnt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN NULL ELSE 1 END), 0) AS cncl_cnt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN WD.CONF_AMT ELSE NULL END), 0) AS conf_amt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN NULL ELSE WD.CONF_AMT END), 0) AS cncl_amt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN 1 ELSE NULL END), 0) AS unremit_cnt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN WD.REMIT_AMT ELSE NULL END), 0) AS unremit_amt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN NULL ELSE 1 END), 0) AS remit_cnt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN NULL ELSE WD.REMIT_AMT END), 0) AS remit_amt FROM TB_WITHDRAW WD STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = WD.CHAIN_NO AND TC.SVC_STAT = 'O' AND TC.USE_YN = 'Y' AND TC.REMIT_STAT = 'Y'
AND TC.AGENCY_CD = #{sch_agency_cd}
AND TC.CORP_CD = #{sch_corp_no}
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_chain_nm}, '%')
WHERE WD.CONF_DTTM >= #{sch_conf_sdt} AND WD.CONF_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59') AND WD.REMIT_NO IS NULL
AND WD.CHAIN_NO = #{sch_chain_no}
GROUP BY SUBSTR(WD.CONF_DTTM, 1, 10), WD.CHAIN_NO, TC.CHAIN_NM , TC.BIZ_NO, TC.REMIT_STAT, WD.WORK_DATE
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}

/* WithdrawMapper.getURSummaryTotal */ SELECT SQL_CALC_FOUND_ROWS IFNULL(COUNT(1), 0) AS tot_cnt , IFNULL(SUM(WD.CONF_AMT), 0) AS tot_amt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN 1 ELSE NULL END), 0) AS conf_cnt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN NULL ELSE 1 END), 0) AS cncl_cnt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN WD.CONF_AMT ELSE NULL END), 0) AS conf_amt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN NULL ELSE WD.CONF_AMT END), 0) AS cncl_amt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN 1 ELSE NULL END), 0) AS unremit_cnt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN WD.REMIT_AMT ELSE NULL END), 0) AS unremit_amt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN NULL ELSE 1 END), 0) AS remit_cnt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN NULL ELSE WD.REMIT_AMT END), 0) AS remit_amt FROM TB_WITHDRAW WD STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = WD.CHAIN_NO AND TC.SVC_STAT = 'O' AND TC.USE_YN = 'Y' AND TC.REMIT_STAT = 'Y'
AND TC.AGENCY_CD = #{sch_agency_cd}
AND TC.CORP_CD = #{sch_corp_no}
AND TC.CHAIN_NM LIKE CONCAT('%', #{sch_chain_nm}, '%')
WHERE WD.CONF_DTTM >= #{sch_conf_sdt} AND WD.CONF_DTTM <= CONCAT(#{sch_conf_edt}, ' 23:59:59')
AND WD.CHAIN_NO = #{sch_chain_no}
AND WD.REMIT_NO IS NULL

/* WithdrawMapper.getURCardSummary */ SELECT SQL_CALC_FOUND_ROWS WD.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', WD.CARD_ACQ) AS card_acq_nm , IFNULL(COUNT(1), 0) AS tot_cnt , IFNULL(SUM(WD.CONF_AMT), 0) AS tot_amt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN 1 ELSE NULL END), 0) AS conf_cnt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN NULL ELSE 1 END), 0) AS cncl_cnt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN WD.CONF_AMT ELSE NULL END), 0) AS conf_amt , IFNULL(SUM(CASE WHEN WD.CONF_GB = 'A' THEN NULL ELSE WD.CONF_AMT END), 0) AS cncl_amt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN 1 ELSE NULL END), 0) AS unremit_cnt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN WD.REMIT_AMT ELSE NULL END), 0) AS unremit_amt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN NULL ELSE 1 END), 0) AS remit_cnt , IFNULL(SUM(CASE WHEN WD.REMIT_NO IS NULL THEN NULL ELSE WD.REMIT_AMT END), 0) AS remit_amt FROM TB_WITHDRAW WD WHERE WD.CONF_DTTM >= #{sch_card_conf_dt} AND WD.CONF_DTTM <= CONCAT(#{sch_card_conf_dt} , ' 23:59:59') AND WD.CHAIN_NO = #{sch_card_chain_no} GROUP BY WD.CARD_ACQ, GET_CODE_NM('CARD_ACQ', WD.CARD_ACQ)
ORDER BY ${orderBy}
ORDER BY 1

/*WithdrawMapper.getUnRemitList*/ SELECT SQL_CALC_FOUND_ROWS WD.WD_NO AS wd_no , WD.WD_STATUS AS wd_status , GET_CODE_NM('WD_STATUS', WD.WD_STATUS) AS wd_status_nm , WD.WORK_DATE AS work_date , WD.CHAIN_NO AS chain_no , TC.CHAIN_NM AS chain_nm , WD.DOCU_SEQ AS docu_seq , VD.VAN_CD AS van_cd , GET_CODE_NM('VAN_CD', VD.VAN_CD) AS van_cd_nm , WD.CARD_ACQ AS card_acq , GET_CODE_NM('CARD_ACQ', WD.CARD_ACQ) AS card_acq_nm , VD.CARD_NO AS card_no , WD.CARD_REG_NO AS card_reg_no , VD.CARD_TYPE AS card_type , GET_CODE_NM('CARD_TYPE', VD.CARD_TYPE) AS card_type_nm , WD.CONF_NO AS conf_no , WD.CONF_GB AS conf_gb , CASE WHEN WD.CONF_GB = 'A' THEN '승인' ELSE '취소' END AS conf_gb_nm , WD.CONF_DTTM AS conf_dttm , WD.CONF_AMT AS conf_amt , WD.CARD_BUY_FLAG AS card_buy_flag , WD.CARD_BUY_DATE AS card_buy_date , WD.CARD_FEE_RATE AS fard_fee_rate , WD.CARD_FEE_AMT AS card_fee_amt , IFNULL(WD.CARD_RESV_AMT, 0) AS card_resv_amt , IFNULL(WD.CARD_RESV_DAY, 0) AS card_resv_day , IFNULL(WD.CARD_RESV_DATE, '') AS card_resv_date , IFNULL(WD.SVC_FEE_RATE, 0) AS svc_fee_rate , IFNULL(WD.SVC_FEE_AMT, 0) AS svc_fee_amt , IFNULL(WD.WD_BASE_AMT, 0) AS wd_base_amt , IFNULL(WD.CRD_FEE_RATE, 0) AS crd_fee_rate , IFNULL(WD.CRD_FEE_AMT, 0) AS crd_fee_amt , IFNULL(WD.SUB_FLAG, 'N') AS sub_flag , IFNULL(WD.REMIT_AMT, 0) AS remit_amt , IFNULL(WD.REMIT_NO, '') AS remit_seq , IFNULL(WD.REMIT_DT, '') AS remit_dt , IFNULL(WD.DEPOSIT_FLAG, '') AS deposit_flag , IFNULL(WD.BANK_IN_DATE, '') AS bank_in_date , IFNULL(WD.CARDSALES_FG, '') AS cardsales_fg , WD.USE_YN AS use_yn , IFNULL(WD.WD_MEMO, '') AS wd_memo , WD.CLOSE_YN AS close_yn , DATE_FORMAT(IFNULL(WD.UPT_DTTM, WD.ENT_DTTM), '%Y-%m-%d %H:%i:%s') AS upt_dttm , DATE_FORMAT(IFNULL(WD.ENT_USER_ID, WD.UPT_USER_ID), '%Y-%m-%d %H:%i:%s') AS upt_user_id FROM TB_WITHDRAW WD STRAIGHT_JOIN TB_VAN_DOCU VD ON VD.DOCU_SEQ = WD.DOCU_SEQ STRAIGHT_JOIN TB_CHAIN TC ON TC.CHAIN_NO = WD.CHAIN_NO AND TC.SVC_STAT = 'O' AND TC.USE_YN = 'Y' AND TC.REMIT_STAT = 'Y' WHERE 1=1 AND WD.CONF_DTTM >= #{sch_list_conf_dt} AND WD.CONF_DTTM <= CONCAT(#{sch_list_conf_dt} , ' 23:59:59') AND WD.REMIT_NO IS NULL AND WD.CHAIN_NO = #{sch_list_chain_no}
AND FIND_IN_SET(WD.CARD_ACQ, #{sch_list_card_acq})
AND VD.VAN_CD = #{sch_list_van}
AND WD.CONF_NO = #{sch_list_gb_conf_val}
AND WD.CARD_NO = #{sch_list_gb_conf_val}
AND WD.CONF_AMT = #{sch_list_gb_conf_val}
ORDER BY ${orderBy}
ORDER BY 1
LIMIT ${start}, ${end}
